          <div key={agent} className="bg-slate-900/50 border border-slate-700/50 rounded-lg p-4 flex items-center gap-4">
            <div className={`w-3 h-3 rounded-full ${agent === 'Alden' ? 'bg-emerald-500 shadow-[0_0_10px_rgba(16,185,129,0.5)]' : 'bg-slate-500'}`} />
            <div>
              <h3 className="font-bold text-slate-200">{agent}</h3>
              <p className="text-xs text-slate-400">{agent === 'Alden' ? 'Active' : 'Idle'}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 min-h-0 flex-1">
        {/* Active Conferences */}
        <div className="bg-slate-900/50 border border-slate-700/50 rounded-xl p-6 flex flex-col overflow-hidden">
          <div className="flex justify-between items-center mb-4 shrink-0">
            <h3 className="text-lg font-semibold text-slate-200 flex items-center gap-2">
              <Users size={20} className="text-blue-400" />
              Active Conferences
            </h3>
            <span className="text-xs bg-blue-500/10 text-blue-400 px-2 py-1 rounded border border-blue-500/20">
              {conferences.filter(c => c.state === 'active').length} Active
            </span>
          </div>
          <div className="flex-1 overflow-y-auto pr-2 space-y-3 scrollbar-thin scrollbar-thumb-slate-700">
            {conferences.filter(c => c.state === 'active').length === 0 ? (
