                <div key={conf.conference_id} className="bg-slate-800/50 p-4 rounded-lg border-l-4 border-emerald-500">
                  <h4 className="font-medium text-slate-200">{conf.title}</h4>
                  <p className="text-sm text-slate-400 mt-1">{conf.purpose}</p>
                  <div className="flex gap-2 mt-3">
                    {conf.participants.map((p, i) => (
                      <span key={i} className="text-xs bg-slate-700 text-slate-300 px-2 py-1 rounded">{p.agent_id}</span>
                    ))}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Event Stream */}
        <div className="bg-slate-900/50 border border-slate-700/50 rounded-xl p-6 flex flex-col overflow-hidden">
          <h3 className="text-lg font-semibold text-slate-200 mb-4 flex items-center gap-2 shrink-0">
            <Activity size={20} className="text-purple-400" />
