                  {event.event_type === 'tool_call' && <Activity size={14} className="text-amber-400" />}
                </div>
                <div>
                  <div className="flex items-center gap-2">
                    <span className="font-medium text-slate-300 capitalize">{event.event_type.replace('_', ' ')}</span>
                    <span className="text-xs text-slate-500">{new Date(event.timestamp).toLocaleTimeString()}</span>
                  </div>
                  <p className="text-slate-400 mt-0.5">
                    {event.event_type === 'message' && `${event.from_agent} ƒ+' ${event.to_agent}`}
                    {event.event_type === 'handoff' && `${event.from_agent} handed off to ${event.to_agent}`}
                    {event.event_type === 'tool_call' && `${event.agent_id} called tool`}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

