/// Get private feed entries for a user across different contexts
#[tauri::command]
pub async fn get_private_feed(
    request: GetPrivateFeedRequest,
) -> Result<Vec<PrivateFeedEntry>, String> {
    // Validate request
    if request.user_id.is_empty() {
        return Err("user_id is required".to_string());
    }

    let limit = request.limit.unwrap_or(100).min(1000); // Max 1000 entries

    let context_filter = parse_contexts(request.context.as_ref())?;

    // In a real implementation, this would query the database
    // For now, return mock data based on context
    let mut entries =
        generate_mock_feed_entries(&request.user_id, context_filter.as_deref(), limit);

    // Optional visibility filtering
    if let Some(filter) = request.visibility.as_deref() {
        let normalized = filter.to_lowercase();
        entries.retain(|entry| match normalized.as_str() {
            "public" => matches!(entry.visibility, FeedVisibility::Public),
            "private" => matches!(entry.visibility, FeedVisibility::Private),
            "log" | "logonly" | "log_only" => matches!(entry.visibility, FeedVisibility::LogOnly),
            _ => true,
        });
    }

    // Optional minimum priority filtering
    if let Some(min_priority) = request.min_priority.as_deref() {
        if let Some(min_level) = parse_priority(min_priority) {
            entries.retain(|entry| priority_level(&entry.priority) >= priority_level(&min_level));
        }
    }

    // Optional time filtering
    if let Some(since) = request.since.as_deref() {
        if let Ok(since_dt) = DateTime::parse_from_rfc3339(since) {
            let since_utc = since_dt.with_timezone(&Utc);
            entries.retain(|entry| {
                DateTime::parse_from_rfc3339(&entry.timestamp)
                    .map(|ts| ts.with_timezone(&Utc) >= since_utc)
                    .unwrap_or(true)
            });
        }
    }

    Ok(entries)
}

