/// Collect a richer system metrics payload for dashboard widgets.
/// Optimized for real-time performance with reduced blocking operations.
#[tauri::command]
pub fn get_system_metrics() -> Result<SystemMetrics, String> {
    // Use a static system instance to avoid repeated initialization overhead
    use std::sync::Mutex;
    use std::sync::OnceLock;

    static SYSTEM_CACHE: OnceLock<Mutex<System>> = OnceLock::new();
    static LAST_REFRESH: OnceLock<Mutex<std::time::Instant>> = OnceLock::new();

    let sys = SYSTEM_CACHE.get_or_init(|| {
        Mutex::new(System::new_with_specifics(
            RefreshKind::new()
                .with_cpu(CpuRefreshKind::cpu_usage())
                .with_memory(MemoryRefreshKind::nothing()),
        ))
    });

    let last_refresh = LAST_REFRESH.get_or_init(|| Mutex::new(std::time::Instant::now()));

    // Only refresh CPU if enough time has passed (reduce blocking)
    {
        let mut sys_guard = sys
            .lock()
            .map_err(|e| format!("System lock error: {}", e))?;
        let mut last_refresh_guard = last_refresh
            .lock()
            .map_err(|e| format!("Timestamp lock error: {}", e))?;

        let now = std::time::Instant::now();
        if now.duration_since(*last_refresh_guard).as_millis() > 500 {
            // Fast refresh - only CPU and basic memory
            sys_guard.refresh_cpu();
            sys_guard.refresh_memory();
            *last_refresh_guard = now;
        }

        let cpu_load = sys_guard.global_cpu_info().cpu_usage();

        // Use cached memory values for performance
        let total_memory = sys_guard.total_memory();
        let used_memory = sys_guard.used_memory();
        let memory_total_mb = total_memory / 1024 / 1024;
        let memory_used_mb = used_memory / 1024 / 1024;
        let memory_used_percent = if total_memory > 0 {
            (used_memory as f32 / total_memory as f32) * 100.0
        } else {
            0.0
        };

        // Cache disk info separately since it changes less frequently
        use std::sync::OnceLock;
        static DISK_CACHE: OnceLock<Mutex<(u64, u64, std::time::Instant)>> = OnceLock::new();

        let (disk_total_mb, disk_used_mb) = {
            let disk_cache =
                DISK_CACHE.get_or_init(|| Mutex::new((0, 0, std::time::Instant::now())));

            let mut disk_guard = disk_cache
                .lock()
                .map_err(|e| format!("Disk cache lock error: {}", e))?;

            // Only refresh disk info every 5 seconds
            if now.duration_since(disk_guard.2).as_secs() > 5 {
                let disks = Disks::new_with_refreshed_list();
                let mut disk_total_bytes = 0;
                let mut disk_used_bytes = 0;
                for disk in &disks {
                    let total = disk.total_space();
                    let available = disk.available_space();
                    disk_total_bytes += total;
                    disk_used_bytes += total - available;
                }
                disk_guard.0 = disk_total_bytes / 1024 / 1024;
                disk_guard.1 = disk_used_bytes / 1024 / 1024;
                disk_guard.2 = now;
            }

            (disk_guard.0, disk_guard.1)
        };

        let disk_used_percent = if disk_total_mb > 0 {
            (disk_used_mb as f32 / disk_total_mb as f32) * 100.0
        } else {
            0.0
        };

        Ok(SystemMetrics {
            cpu_load,
            memory_used_mb,
            memory_total_mb,
            memory_used_percent,
            disk_used_mb,
            disk_total_mb,
            disk_used_percent,
            process_count: 0, // Skipping processes for real-time performance
            uptime_seconds: System::uptime(),
            timestamp: Utc::now().to_rfc3339(),
            source: "native_optimized".to_string(),
        })
    }
}

fn parse_contexts(raw: Option<&Vec<String>>) -> Result<Option<Vec<FeedContext>>, String> {
    let contexts = match raw {
        Some(list) if !list.is_empty() => list,
        _ => return Ok(None),
    };

    let mut parsed = Vec::new();
    for ctx in contexts {
        match ctx.to_lowercase().as_str() {
            "core" => parsed.push(FeedContext::Core),
            "vault" => parsed.push(FeedContext::Vault),
            "synapse" => parsed.push(FeedContext::Synapse),
            other => return Err(format!("Invalid context: {}", other)),
        }
    }

    Ok(Some(parsed))
}

fn parse_priority(raw: &str) -> Option<FeedPriority> {
    match raw.to_lowercase().as_str() {
        "low" => Some(FeedPriority::Low),
        "medium" | "med" => Some(FeedPriority::Medium),
        "high" => Some(FeedPriority::High),
        "critical" | "crit" => Some(FeedPriority::Critical),
        _ => None,
    }
}

fn priority_level(priority: &FeedPriority) -> u8 {
    match priority {
        FeedPriority::Low => 1,
        FeedPriority::Medium => 2,
        FeedPriority::High => 3,
        FeedPriority::Critical => 4,
    }
}

fn deserialize_contexts<'de, D>(deserializer: D) -> Result<Option<Vec<String>>, D::Error>
where
    D: Deserializer<'de>,
{
    #[derive(Deserialize)]
    #[serde(untagged)]
    enum StringOrVec {
        String(String),
        Vec(Vec<String>),
    }

    let value = Option::<StringOrVec>::deserialize(deserializer)?;
    Ok(value.map(|v| match v {
        StringOrVec::String(s) => vec![s],
        StringOrVec::Vec(list) => list,
    }))
}

