         <div className="flex-1 overflow-y-auto p-2 space-y-1 scrollbar-thin scrollbar-thumb-red-900/50">
            {[...Array(20)].map((_, i) => (
               <div key={i} className="grid grid-cols-12 gap-4 p-2 hover:bg-red-900/10 rounded transition-colors border-b border-red-900/10 last:border-0">
                  <div className="col-span-2 text-red-500/60 text-xs">{new Date().toISOString().split('T')[0]} 10:4{i}:22</div>
                  <div className="col-span-1">
                     <span className={`text-[10px] px-1.5 py-0.5 rounded border ${
