/**
 * Alice Deep Work Component - Distraction-Free Focus Timer
 *
 * Features:
 * - 25-minute Pomodoro-style focus timer
 * - Backend integration for session tracking
 * - Accessibility features (ARIA labels, keyboard shortcuts)
 * - ADHD optimizations (completion celebration, visual/audio feedback)
 * - Sound toggle for ambient focus music
 *
 * @version 2.0.0
 * @date 2025-12-02
 */

import React, { useState, useEffect, useCallback } from 'react';
import { Clock, Play, Pause, RotateCcw, Volume2, VolumeX, Sparkles } from 'lucide-react';
import confetti from 'canvas-confetti';
import { toast } from 'sonner';
import { useAliceBridge } from '../../../hooks/useBridge';

export const AliceDeepWork: React.FC = () => {
  const [timeLeft, setTimeLeft] = useState(25 * 60); // 25 minutes
  const [isActive, setIsActive] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { bridge, loading: bridgeLoading, error: bridgeError } = useAliceBridge();

  const [analysis, setAnalysis] = useState<any>(null);

  // Completion celebration
  const triggerCompletionCelebration = useCallback(async () => {
    // Confetti animation (Heavy Gold & Cyan - MythologIQ Design System)
    try {
      confetti({
        particleCount: 150,
        spread: 90,
        origin: { y: 0.5 },
        colors: ['#FFD700', '#00D4FF', '#3399FF'], // Metal Gold, Mythiq Cyan, Electric Blue
        gravity: 1.2,
        decay: 0.94,
        ticks: 250,
      });
    } catch (err) {
      console.warn('[AliceDeepWork] Confetti animation failed:', err);
    }

    // Success toast
    toast.success('Deep Work Session Complete! dYZ%', {
      description: 'You maintained focus for 25 minutes. Great job!',
      duration: 5000,
    });

    // Optional success sound
    if (!isMuted) {
      try {
        const audio = new Audio('/sounds/success.mp3');
        audio.volume = 0.3;
        audio.play().catch(() => {
          // Silent fail
        });
      } catch (err) {
        console.warn('[AliceDeepWork] Audio playback failed:', err);
      }
    }

    // Fetch analysis
    if (bridge) {
      try {
        const result = await bridge.getDeepWorkAnalysis();
        if (result.success) {
          setAnalysis(result.data);
        }
      } catch (err) {
        console.error('[AliceDeepWork] Failed to fetch analysis:', err);
      }
    }
  }, [isMuted, bridge]);

  // Timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isActive && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timeLeft === 0 && isActive) {
      setIsActive(false);
      if (bridge) {
        bridge.stopDeepWork('completed').catch((err) => {
          console.error('[AliceDeepWork] Failed to stop session:', err);
          setError('Failed to save session completion');
        });
      }
      triggerCompletionCelebration();
    }
    return () => clearInterval(interval);
  }, [isActive, timeLeft, bridge, triggerCompletionCelebration]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      // Space to toggle timer
      if (e.code === 'Space' && !e.repeat) {
        e.preventDefault();
        toggleTimer();
      }
      // R to reset
      if (e.code === 'KeyR' && !e.repeat) {
        e.preventDefault();
        resetTimer();
      }
      // M to toggle mute
      if (e.code === 'KeyM' && !e.repeat) {
        e.preventDefault();
        setIsMuted(prev => !prev);
      }
      // Esc to close analysis
      if (e.code === 'Escape' && analysis) {
        setAnalysis(null);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [analysis]); // eslint-disable-line react-hooks/exhaustive-deps

  const toggleTimer = useCallback(async () => {
    if (!bridge || isLoading) return;

    setIsLoading(true);
    setError(null);

    try {
      if (!isActive) {
        // Starting
        await bridge.startDeepWork();
        setIsActive(true);
        toast.info('Deep Work Session Started', {
          description: '25 minutes of focused work. You got this!',
          duration: 3000,
        });
      } else {
        // Pausing/Stopping
        await bridge.stopDeepWork('aborted');
        setIsActive(false);
        toast.info('Deep Work Session Paused', {
          description: 'Take a break when you need it.',
          duration: 3000,
        });
        // Fetch analysis even on abort? Maybe not for now.
      }
    } catch (err) {
      console.error('[AliceDeepWork] Toggle failed:', err);
      const errorMsg = err instanceof Error ? err.message : 'Failed to toggle session';
      setError(errorMsg);
      toast.error('Session Error', {
        description: errorMsg,
        duration: 4000,
      });
    } finally {
      setIsLoading(false);
    }
  }, [bridge, isActive, isLoading]);

  const resetTimer = useCallback(async () => {
    if (isLoading) return;

    setIsLoading(true);
    setError(null);
    setAnalysis(null);

    try {
      if (isActive && bridge) {
        await bridge.stopDeepWork('aborted');
      }
      setIsActive(false);
      setTimeLeft(25 * 60);
      toast.info('Timer Reset', {
        description: 'Ready for a fresh start.',
        duration: 2000,
      });
    } catch (err) {
      console.error('[AliceDeepWork] Reset failed:', err);
      const errorMsg = err instanceof Error ? err.message : 'Failed to reset timer';
      setError(errorMsg);
      toast.error('Reset Error', {
        description: errorMsg,
        duration: 4000,
      });
    } finally {
      setIsLoading(false);
    }
  }, [isActive, bridge, isLoading]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Loading state
  if (bridgeLoading) {
    return (
      <div className="flex flex-col items-center justify-center h-[70vh] space-y-6">
        <Sparkles className="w-12 h-12 text-[hsl(var(--mythiq-cyan))] animate-pulse" />
        <p className="text-[hsl(var(--metal-gold))] text-sm uppercase tracking-widest">Initializing Deep Work...</p>
      </div>
    );
  }

  return (
    <div
      className="flex flex-col items-center justify-center h-[70vh] space-y-12 relative overflow-hidden"
      role="main"
      aria-label="Deep Work Timer"
    >
      {/* Background Image with Navy Overlay */}
      <div
        className="absolute inset-0 z-0 opacity-30"
        style={{
          backgroundImage: 'url(/images/alice-deepwork-bg-1.png)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        <div className="absolute inset-0 bg-gradient-to-b from-[#020617]/80 via-[#0f172a]/70 to-[#020617]/90" />
      </div>

      {/* Content Layer */}
      <div className="relative z-10 flex flex-col items-center justify-center space-y-12 w-full">
        {/* Analysis Overlay */}
        {analysis && (
          <div className="absolute inset-0 z-50 bg-background/95 backdrop-blur-md flex items-center justify-center p-8 animate-in fade-in zoom-in duration-300">
            <div className="max-w-2xl w-full space-y-8 text-center">
              <h3 className="text-3xl font-orbitron text-white tracking-widest mb-6">SESSION ANALYSIS</h3>

              <div className="grid grid-cols-3 gap-6">
                <div className="p-6 rounded-lg bg-white/5 border border-white/10">
                  <div className="text-[hsl(var(--mythiq-cyan))] text-4xl font-bold mb-2">
                    {Math.round(analysis.summary?.avgFocusScore || 0)}%
                  </div>
                  <div className="text-xs uppercase tracking-widest text-white/50">Focus Score</div>
                </div>
                <div className="p-6 rounded-lg bg-white/5 border border-white/10">
                  <div className="text-emerald-400 text-4xl font-bold mb-2">
                    {Math.round(analysis.summary?.avgSessionQuality || 0)}%
                  </div>
                  <div className="text-xs uppercase tracking-widest text-white/50">Quality</div>
                </div>
                <div className="p-6 rounded-lg bg-white/5 border border-white/10">
                  <div className="text-rose-400 text-4xl font-bold mb-2">
                    {Math.round(analysis.patterns?.attention?.avgDistractions || 0)}
                  </div>
                  <div className="text-xs uppercase tracking-widest text-white/50">Distractions</div>
                </div>
              </div>

              {analysis.insights && analysis.insights.length > 0 && (
                <div className="text-left bg-white/5 p-6 rounded-lg border border-white/10">
                  <h4 className="text-[hsl(var(--metal-gold))] text-sm uppercase tracking-widest mb-4">Key Insight</h4>
                  <p className="text-white/80">{analysis.insights[0].description}</p>
                  <p className="text-white/60 text-sm mt-2 italic">{analysis.insights[0].recommendation}</p>
                </div>
              )}

              <button
                onClick={() => setAnalysis(null)}
                className="px-8 py-3 bg-[hsl(var(--mythiq-cyan))] hover:bg-[hsl(var(--mythiq-electric-blue))] text-gray-900 rounded-full transition-all uppercase tracking-widest text-sm font-bold"
              >
                Close Analysis
              </button>
            </div>
          </div>
        )}

        <div className="text-center">
          <h2 className="text-4xl font-orbitron text-[hsl(var(--metal-gold))] mb-2 tracking-widest">DEEP WORK</h2>
          <p className="text-[hsl(var(--mythiq-cyan))] uppercase tracking-widest text-sm">Distraction-Free Environment</p>
          <p className="text-white/40 text-xs mt-2">
            Keyboard: <kbd className="px-2 py-1 bg-white/5 rounded">Space</kbd> toggle, <kbd className="px-2 py-1 bg-white/5 rounded">R</kbd> reset, <kbd className="px-2 py-1 bg-white/5 rounded">M</kbd> mute
          </p>
        </div>

        {/* Error Message */}
        {(error || bridgeError) && (
          <div
            className="px-4 py-2 bg-rose-500/10 border border-rose-500/30 rounded-lg text-rose-400 text-sm max-w-md text-center"
            role="alert"
            aria-live="polite"
          >
            {error || (bridgeError ? bridgeError.message : '')}
          </div>
        )}

        {/* Timer Circle */}
        <div className="relative w-80 h-80 flex items-center justify-center" aria-live="polite" aria-atomic="true">
          {/* Outer Ring */}
          <div className={`absolute inset-0 rounded-full border-4 transition-all duration-300 ${
            isActive
              ? 'border-[hsl(var(--mythiq-cyan))] animate-pulse shadow-[0_0_30px_rgba(0,212,255,0.5)]'
              : 'border-white/10'
          }`}></div>

          {/* Inner Ring */}
          <div className="absolute inset-4 rounded-full border border-white/5"></div>

          {/* Progress Indicator */}
          {isActive && (
            <div
              className="absolute inset-0 rounded-full"
              style={{
                background: `conic-gradient(
                  from 0deg,
                  hsl(var(--mythiq-cyan)) 0%,
                  hsl(var(--mythiq-cyan)) ${((1500 - timeLeft) / 1500) * 100}%,
                  transparent ${((1500 - timeLeft) / 1500) * 100}%
                )`,
                opacity: 0.15,
              }}
            ></div>
          )}

          {/* Time Display */}
          <div
            className="text-7xl font-bold font-orbitron text-white tabular-nums tracking-tighter"
            aria-label={`Time remaining: ${formatTime(timeLeft)}`}
          >
            {formatTime(timeLeft)}
          </div>

          {/* Completion Badge */}
          {timeLeft === 0 && (
            <div className="absolute -bottom-4 bg-[hsl(var(--metal-gold))] text-gray-900 px-4 py-1 rounded-full text-xs uppercase tracking-widest animate-bounce font-bold">
              Complete!
            </div>
          )}
        </div>

        {/* Controls */}
        <div className="flex items-center gap-8">
          <button
            onClick={toggleTimer}
            disabled={isLoading || !bridge}
            className={`w-16 h-16 rounded-full flex items-center justify-center transition-all disabled:opacity-50 disabled:cursor-not-allowed ${
              isActive
                ? 'bg-rose-500 text-white hover:bg-rose-600 shadow-[0_0_20px_rgba(239,68,68,0.5)]'
                : 'bg-[hsl(var(--mythiq-cyan))] text-gray-900 hover:bg-[hsl(var(--mythiq-electric-blue))] shadow-[0_0_20px_rgba(0,212,255,0.5)]'
            }`}
            aria-label={isActive ? 'Pause timer' : 'Start timer'}
            title={isActive ? 'Pause (Space)' : 'Start (Space)'}
          >
            {isLoading ? (
              <Sparkles size={32} className="animate-spin" />
            ) : isActive ? (
              <Pause size={32} />
            ) : (
              <Play size={32} className="ml-1" />
            )}
          </button>

          <button
            onClick={resetTimer}
            disabled={isLoading}
            className="w-12 h-12 rounded-full bg-white/5 hover:bg-white/10 flex items-center justify-center text-slate-400 hover:text-white transition-all disabled:opacity-50 disabled:cursor-not-allowed"
            aria-label="Reset timer"
            title="Reset (R)"
          >
            <RotateCcw size={20} />
          </button>

          <button
            onClick={() => setIsMuted(!isMuted)}
            className={`w-12 h-12 rounded-full flex items-center justify-center transition-all ${
              isMuted ? 'bg-white/5 text-slate-500' : 'bg-[hsl(var(--metal-gold))]/20 text-[hsl(var(--metal-gold))] border border-[hsl(var(--metal-gold))]/30'
            }`}
            aria-label={isMuted ? 'Unmute sounds' : 'Mute sounds'}
            aria-pressed={!isMuted}
            title={`${isMuted ? 'Unmute' : 'Mute'} (M)`}
          >
            {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
          </button>
        </div>

        {/* Session Info */}
        <div className="text-center text-white/30 text-xs space-y-1">
          <p>25-minute focus session ƒ?› Pomodoro technique</p>
          <p className="text-white/20">Session data is saved automatically</p>
        </div>
      </div>
    </div>
  );
};

