+/// Lightweight log retrieval for Sentry Command Center (supports polling)
+#[tauri::command]
+pub async fn sentry_get_security_logs(
+    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
+    user_id: Option<String>,
+    severity: Option<String>,
+    limit: Option<i64>,
+) -> Result<Vec<SentrySecurityEvent>, String> {
+    let db = db.read().await;
+    let db = db.as_ref().ok_or("Database not initialized")?;
+
+    let mut builder: QueryBuilder<Sqlite> = QueryBuilder::new(
+        "SELECT event_id, user_id, agent_id, event_type, severity, description, details, timestamp, resolved, resolution FROM sentry_security_events WHERE 1=1"
+    );
+
+    if let Some(uid) = &user_id {
+        builder.push(" AND user_id = ");
+        builder.push_bind(uid);
+    }
+
+    if let Some(sev) = &severity {
+        builder.push(" AND severity = ");
+        builder.push_bind(sev);
+    }
+
+    builder.push(" ORDER BY timestamp DESC");
+    if let Some(l) = limit {
+        builder.push(" LIMIT ");
+        builder.push_bind(l.max(1));
+    } else {
+        builder.push(" LIMIT 200");
+    }
+
+    let rows = builder
+        .build_query_as::<SentrySecurityEventRow>()
+        .fetch_all(&db.pool)
+        .await
+        .map_err(|e| format!("Failed to fetch security logs: {}", e))?;
+
+    rows.into_iter().map(map_security_event_row).collect()
+}
