/**
 * AvatarStudio - Avatar creation tab for Mimic Module
 * Embeds ReadyPlayer.me avatar creator as an integrated experience
 */
import React, { memo, useCallback, useState, useMemo } from 'react';
import { User, Sparkles, Check, RefreshCw, Download, Upload, Trash2 } from 'lucide-react';
import { AvatarCreator, AvatarCreatorConfig, AvatarExportedEvent } from '@readyplayerme/react-avatar-creator';
import { Avatar3D } from '../avatar/Avatar3D';
import { useAvatarStore } from '../avatar/useAvatarStore';
import { RPM_SUBDOMAIN, DEFAULT_QUALITY_SETTINGS } from '../avatar/types';
import type { PersonaAvatarConfig } from '../avatar/types';

import { AgentAvatar } from '../shared/chat/AgentAvatar';
import { SYSTEM_PERSONAS as SHARED_SYSTEM_PERSONAS } from '../shared/chat/types';
import type { Persona } from '../shared/chat/types';

// Map shared personas to the format needed for the studio, or use them directly
const STUDIO_PERSONAS = Object.values(SHARED_SYSTEM_PERSONAS).map(p => ({
  ...p,
  color: p.id === 'alden' ? '#d6b369' : p.id === 'alice' ? '#00e6ff' : p.id === 'sentry' ? '#ef4444' : '#a855f7',
  status: 'idle' as const // Default status for preview
}));

// Build the avatar URL with quality parameters
const buildAvatarUrl = (baseUrl: string): string => {
  const url = new URL(baseUrl);

  // CRITICAL: Clear all existing query parameters to prevent 400 Bad Request errors
  // The ReadyPlayer.me creator often returns URLs with pre-populated params that can be invalid
  url.search = '';

  // Add ONLY the essential, safe parameters:
  // - morphTargets=ARKit: Required for lip-sync functionality
  // - textureSizeLimit=1024: Safe default for performance
  url.searchParams.set('morphTargets', DEFAULT_QUALITY_SETTINGS.morphTargets);
  url.searchParams.set('textureSizeLimit', String(DEFAULT_QUALITY_SETTINGS.textureSizeLimit));

  // DO NOT add these parameters as they can cause 400 errors:
  // - meshLod, textureAtlas, useDracoMeshCompression, useHands

  return url.toString();
};

// Extract avatar ID from URL
const extractAvatarId = (url: string): string | undefined => {
  try {
    const match = url.match(/\/([a-f0-9-]+)\.glb/i);
    return match?.[1];
  } catch {
    return undefined;
  }
};

interface PersonaSelectorProps {
  personas: typeof STUDIO_PERSONAS;
  selectedId: string;
  onSelect: (id: string) => void;
  avatarStore: ReturnType<typeof useAvatarStore>;
}

const PersonaSelector = memo<PersonaSelectorProps>(({ personas, selectedId, onSelect, avatarStore }) => (
  <div className="space-y-2">
    <h3 className="text-xs font-bold text-[#a855f7] uppercase tracking-widest mb-3">
      Select Persona
    </h3>
    <div className="space-y-1">
      {personas.map(persona => {
        const hasAvatar = avatarStore.hasCustomAvatar(persona.id);
        const isSelected = selectedId === persona.id;

        return (
          <button
            key={persona.id}
            onClick={() => onSelect(persona.id)}
            className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-lg transition-all ${
              isSelected
                ? 'bg-[#a855f7]/20 border border-[#a855f7]/50'
                : 'bg-slate-800/50 border border-transparent hover:border-[#a855f7]/30 hover:bg-slate-800'
            }`}
          >
            <div className="relative">
              <AgentAvatar
                persona={persona as Persona}
                size="sm"
                showStatus={false}
                showBorder={isSelected}
                className={isSelected ? 'ring-2 ring-[#a855f7]' : ''}
              />
              {hasAvatar && (
                <div className="absolute -bottom-1 -right-1 bg-green-500 rounded-full p-0.5 border border-[#0a0f1d]">
                  <Check size={8} className="text-white" />
                </div>
              )}
            </div>
            
            <div className="flex-1 text-left">
              <div className="text-sm font-semibold text-white">{persona.name}</div>
              <div className="text-[10px] text-slate-400 uppercase tracking-wider">{persona.subtitle}</div>
            </div>
          </button>
        );
      })}
    </div>
  </div>
));

PersonaSelector.displayName = 'PersonaSelector';

interface AvatarPreviewProps {
  personaId: string;
  personaName: string;
  avatarUrl?: string;
  onRemove: () => void;
  onEdit: () => void;
}

const AvatarPreview = memo<AvatarPreviewProps>(({ personaId, personaName, avatarUrl, onRemove, onEdit }) => (
  <div className="bg-slate-900/50 rounded-xl p-6 border border-white/10">
    <div className="flex items-center justify-between mb-4">
      <h3 className="text-sm font-bold text-white uppercase tracking-wider">Current Avatar</h3>
      {avatarUrl && (
        <div className="flex gap-2">
          <button
            onClick={onEdit}
            className="p-2 rounded-lg bg-[#a855f7]/20 text-[#a855f7] hover:bg-[#a855f7]/30 transition-colors"
            title="Edit avatar"
          >
            <RefreshCw size={14} />
          </button>
          <button
            onClick={onRemove}
            className="p-2 rounded-lg bg-red-500/20 text-red-400 hover:bg-red-500/30 transition-colors"
            title="Remove avatar"
          >
            <Trash2 size={14} />
          </button>
        </div>
      )}
    </div>

    <div className="flex flex-col items-center">
      {avatarUrl ? (
        <Avatar3D
          avatarUrl={avatarUrl}
          animationState="idle"
          size="xl"
          showBackground={true}
        />
      ) : (
        <div className="w-40 h-40 rounded-full bg-slate-800 border-2 border-dashed border-slate-600 flex flex-col items-center justify-center">
          <User size={48} className="text-slate-500 mb-2" />
          <span className="text-xs text-slate-500">No avatar set</span>
        </div>
      )}
      <p className="mt-4 text-sm text-slate-300">{personaName}</p>
      <p className="text-[10px] text-slate-500 uppercase tracking-wider">
        {avatarUrl ? 'Ready Player Me Avatar' : 'Click "Create Avatar" below'}
      </p>
    </div>
  </div>
));

AvatarPreview.displayName = 'AvatarPreview';

export interface AvatarStudioProps {
  className?: string;
}

export const AvatarStudio = memo<AvatarStudioProps>(({ className = '' }) => {
  const [selectedPersonaId, setSelectedPersonaId] = useState<string>('alden');
  const [isCreating, setIsCreating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const avatarStore = useAvatarStore();

  const selectedPersona = useMemo(
    () => STUDIO_PERSONAS.find(p => p.id === selectedPersonaId) || STUDIO_PERSONAS[0],
    [selectedPersonaId]
  );

  const currentAvatarConfig = useMemo(
    () => avatarStore.getAvatarConfig(selectedPersonaId),
    [avatarStore, selectedPersonaId]
  );

  // Avatar creator configuration
  const creatorConfig: AvatarCreatorConfig = useMemo(() => ({
    clearCache: !currentAvatarConfig,
    bodyType: 'fullbody',
    quickStart: false,
    language: 'en',
  }), [currentAvatarConfig]);

  // Handle successful avatar export
  const handleAvatarExported = useCallback((event: AvatarExportedEvent) => {
    try {
      const baseUrl = event.data.url;
      const avatarUrl = buildAvatarUrl(baseUrl);
      const avatarId = extractAvatarId(baseUrl);

      const config: PersonaAvatarConfig = {
        personaId: selectedPersonaId,
        avatarUrl,
        avatarId,
        thumbnailUrl: baseUrl.replace('.glb', '.png'),
        createdAt: currentAvatarConfig?.createdAt || new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };

      avatarStore.setAvatar(config);
      setIsCreating(false);
    } catch (err) {
      console.error('Failed to process avatar:', err);
    }
  }, [selectedPersonaId, currentAvatarConfig, avatarStore]);

  const handleRemoveAvatar = useCallback(() => {
    avatarStore.removeAvatar(selectedPersonaId);
  }, [selectedPersonaId, avatarStore]);

  const handleExport = useCallback(() => {
    const data = avatarStore.exportAvatars();
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `hearthlink-avatars-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);
  }, [avatarStore]);

  const handleImport = useCallback(() => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (ev) => {
          const content = ev.target?.result as string;
          avatarStore.importAvatars(content);
        };
        reader.readAsText(file);
      }
    };
    input.click();
  }, [avatarStore]);

  // Auto-dismiss loading state
  React.useEffect(() => {
    if (isCreating) {
      const timer = setTimeout(() => setIsLoading(false), 2000);
      return () => clearTimeout(timer);
    } else {
      setIsLoading(true);
    }
  }, [isCreating]);

  return (
    <div className={`flex flex-col h-full ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-6 shrink-0">
        <h2 className="text-xl font-semibold text-white flex items-center gap-2">
          <Sparkles size={20} className="text-[#a855f7]" />
          Avatar Studio
        </h2>
        <div className="flex gap-2">
          <button
            onClick={handleImport}
            className="px-3 py-1.5 text-xs font-medium rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-slate-600 transition-all flex items-center gap-1.5"
          >
            <Upload size={12} />
            Import
          </button>
          <button
            onClick={handleExport}
            className="px-3 py-1.5 text-xs font-medium rounded-lg bg-slate-800 text-slate-300 border border-slate-700 hover:border-slate-600 transition-all flex items-center gap-1.5"
          >
            <Download size={12} />
            Export
          </button>
        </div>
      </div>

      {isCreating ? (
        /* Avatar Creator View */
        <div className="flex-1 flex flex-col rounded-xl overflow-hidden border border-[#a855f7]/30 bg-[#0a0f1d] min-h-0">
          {/* Creator Header */}
          <div className="flex items-center justify-between px-4 py-3 border-b border-[#a855f7]/20 bg-[#06101f]/80 shrink-0">
            <div className="flex items-center gap-3">
              <div
                className="w-8 h-8 rounded-full flex items-center justify-center"
                style={{ backgroundColor: `${selectedPersona.color}20`, border: `1px solid ${selectedPersona.color}50` }}
              >
                <Sparkles size={14} style={{ color: selectedPersona.color }} />
              </div>
              <div>
                <span className="text-sm font-bold text-white">Creating Avatar for {selectedPersona.name}</span>
                <p className="text-[10px] text-slate-400 uppercase tracking-wider">Powered by Ready Player Me</p>
              </div>
            </div>
            <button
              onClick={() => setIsCreating(false)}
              className="px-4 py-1.5 text-xs font-medium rounded-lg bg-slate-800 text-slate-300 hover:bg-slate-700 transition-colors"
            >
              Cancel
            </button>
          </div>

          {/* Creator iFrame */}
          <div className="flex-1 relative min-h-0">
            {isLoading && (
              <div className="absolute inset-0 z-10 flex flex-col items-center justify-center bg-[#0a0f1d]">
                <div className="w-10 h-10 border-2 border-[#a855f7] border-t-transparent rounded-full animate-spin mb-4" />
                <p className="text-slate-400 text-sm uppercase tracking-wider">Loading Avatar Creator...</p>
              </div>
            )}
            <AvatarCreator
              subdomain={RPM_SUBDOMAIN}
              config={creatorConfig}
              style={{
                width: '100%',
                height: '100%',
                border: 'none',
                display: isLoading ? 'none' : 'block',
              }}
              onAvatarExported={handleAvatarExported}
            />
          </div>

          {/* Footer */}
          <div className="px-4 py-2 border-t border-[#a855f7]/10 bg-[#06101f]/50 shrink-0">
            <p className="text-[10px] text-slate-500 text-center">
              Customize your avatar's appearance, then click "Next" to save ƒ?› Avatars are stored locally
            </p>
          </div>
        </div>
      ) : (
        /* Selection & Preview View */
        <div className="flex-1 grid grid-cols-[280px_1fr] gap-6 min-h-0">
          {/* Left Column: Persona Selector */}
          <div className="flex flex-col gap-6 overflow-y-auto pr-2 scrollbar-thin scrollbar-thumb-purple-900/50">
            <PersonaSelector
              personas={STUDIO_PERSONAS}
              selectedId={selectedPersonaId}
              onSelect={setSelectedPersonaId}
              avatarStore={avatarStore}
            />

            {/* Quick Stats */}
            <div className="bg-slate-900/50 rounded-xl p-4 border border-white/10">
              <h3 className="text-xs font-bold text-slate-400 uppercase tracking-widest mb-3">
                Avatar Status
              </h3>
              <div className="grid grid-cols-2 gap-3">
                <div className="text-center p-3 bg-slate-800/50 rounded-lg">
                  <span className="text-2xl font-bold text-white">
                    {STUDIO_PERSONAS.filter(p => avatarStore.hasCustomAvatar(p.id)).length}
                  </span>
                  <span className="block text-[10px] text-slate-500 uppercase tracking-wider mt-1">
                    Created
                  </span>
                </div>
                <div className="text-center p-3 bg-slate-800/50 rounded-lg">
                  <span className="text-2xl font-bold text-white">
                    {STUDIO_PERSONAS.length - STUDIO_PERSONAS.filter(p => avatarStore.hasCustomAvatar(p.id)).length}
                  </span>
                  <span className="block text-[10px] text-slate-500 uppercase tracking-wider mt-1">
                    Remaining
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Right Column: Preview & Actions */}
          <div className="flex flex-col gap-6 overflow-y-auto">
            <AvatarPreview
              personaId={selectedPersonaId}
              personaName={selectedPersona.name}
              avatarUrl={currentAvatarConfig?.avatarUrl}
              onRemove={handleRemoveAvatar}
              onEdit={() => setIsCreating(true)}
            />

            {/* Create/Edit Button */}
            <button
              onClick={() => setIsCreating(true)}
              className="w-full py-4 text-sm font-bold rounded-xl bg-[#a855f7]/20 text-[#a855f7] border border-[#a855f7]/50 hover:bg-[#a855f7]/30 transition-all flex items-center justify-center gap-2"
            >
              <Sparkles size={18} />
              {currentAvatarConfig ? 'Edit Avatar' : 'Create Avatar'}
            </button>

            {/* Info */}
            <div className="bg-slate-900/30 rounded-xl p-4 border border-white/5">
              <h4 className="text-xs font-bold text-slate-400 uppercase tracking-widest mb-2">
                About 3D Avatars
              </h4>
              <p className="text-xs text-slate-500 leading-relaxed">
                Create personalized 3D avatars for each AI persona using Ready Player Me.
                Avatars support lip-sync animation and will appear in chat interfaces
                when Dynamic mode is enabled. All avatars are stored locally on your device.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
});

AvatarStudio.displayName = 'AvatarStudio';

export default AvatarStudio;

