/**
 * useAvatarStore - Hook for managing persona avatar configurations
 * Persists avatar URLs to localStorage for offline/local-first use
 */
import { useState, useCallback, useEffect } from 'react';
import type {
  PersonaAvatarConfig,
  AvatarStore,
  AvatarDisplaySettings,
} from './types';
import {
  DEFAULT_PERSONA_AVATARS,
  DEFAULT_DISPLAY_SETTINGS,
} from './types';

const STORAGE_KEY = 'hearthlink_avatar_store';

// Load store from localStorage
const loadStore = (): AvatarStore => {
  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      const parsed = JSON.parse(stored);
      return {
        avatars: parsed.avatars || {},
        defaultAvatars: DEFAULT_PERSONA_AVATARS,
        displaySettings: { ...DEFAULT_DISPLAY_SETTINGS, ...parsed.displaySettings },
      };
    }
  } catch (err) {
    console.warn('Failed to load avatar store:', err);
  }

  return {
    avatars: {},
    defaultAvatars: DEFAULT_PERSONA_AVATARS,
    displaySettings: DEFAULT_DISPLAY_SETTINGS,
  };
};

// Save store to localStorage
const saveStore = (store: AvatarStore): void => {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify({
      avatars: store.avatars,
      displaySettings: store.displaySettings,
    }));
  } catch (err) {
    console.warn('Failed to save avatar store:', err);
  }
};

export interface UseAvatarStoreReturn {
  // State
  avatars: Record<string, PersonaAvatarConfig>;
  displaySettings: AvatarDisplaySettings;
  isLoading: boolean;

  // Actions
  getAvatarUrl: (personaId: string) => string;
  getAvatarConfig: (personaId: string) => PersonaAvatarConfig | null;
  setAvatar: (config: PersonaAvatarConfig) => void;
  removeAvatar: (personaId: string) => void;
  updateDisplaySettings: (settings: Partial<AvatarDisplaySettings>) => void;
  hasCustomAvatar: (personaId: string) => boolean;

  // Bulk operations
  exportAvatars: () => string;
  importAvatars: (jsonData: string) => boolean;
}

export function useAvatarStore(): UseAvatarStoreReturn {
  const [store, setStore] = useState<AvatarStore>(loadStore);
  const [isLoading, setIsLoading] = useState(true);

  // Initialize on mount
  useEffect(() => {
    setStore(loadStore());
    setIsLoading(false);
  }, []);

  // Save to localStorage whenever store changes
  useEffect(() => {
    if (!isLoading) {
      saveStore(store);
    }
  }, [store, isLoading]);

  // Get avatar URL for a persona (custom or default)
  const getAvatarUrl = useCallback((personaId: string): string => {
    const customAvatar = store.avatars[personaId];
    if (customAvatar?.avatarUrl) {
      return customAvatar.avatarUrl;
    }
    return store.defaultAvatars[personaId] || DEFAULT_PERSONA_AVATARS.mimic;
  }, [store.avatars, store.defaultAvatars]);

  // Get full avatar config
  const getAvatarConfig = useCallback((personaId: string): PersonaAvatarConfig | null => {
    return store.avatars[personaId] || null;
  }, [store.avatars]);

  // Check if persona has a custom avatar
  const hasCustomAvatar = useCallback((personaId: string): boolean => {
    return !!store.avatars[personaId]?.avatarUrl;
  }, [store.avatars]);

  // Set/update avatar for a persona
  const setAvatar = useCallback((config: PersonaAvatarConfig): void => {
    setStore(prev => ({
      ...prev,
      avatars: {
        ...prev.avatars,
        [config.personaId]: config,
      },
    }));
  }, []);

  // Remove custom avatar (revert to default)
  const removeAvatar = useCallback((personaId: string): void => {
    setStore(prev => {
      const { [personaId]: removed, ...remaining } = prev.avatars;
      return {
        ...prev,
        avatars: remaining,
      };
    });
  }, []);

  // Update display settings
  const updateDisplaySettings = useCallback((settings: Partial<AvatarDisplaySettings>): void => {
    setStore(prev => ({
      ...prev,
      displaySettings: {
        ...prev.displaySettings,
        ...settings,
      },
    }));
  }, []);

  // Export all avatars as JSON (for backup)
  const exportAvatars = useCallback((): string => {
    return JSON.stringify({
      avatars: store.avatars,
      displaySettings: store.displaySettings,
      exportedAt: new Date().toISOString(),
      version: 1,
    }, null, 2);
  }, [store]);

  // Import avatars from JSON
  const importAvatars = useCallback((jsonData: string): boolean => {
    try {
      const data = JSON.parse(jsonData);
      if (data.avatars && typeof data.avatars === 'object') {
        setStore(prev => ({
          ...prev,
          avatars: { ...prev.avatars, ...data.avatars },
          displaySettings: data.displaySettings
            ? { ...prev.displaySettings, ...data.displaySettings }
            : prev.displaySettings,
        }));
        return true;
      }
      return false;
    } catch (err) {
      console.error('Failed to import avatars:', err);
      return false;
    }
  }, []);

  return {
    avatars: store.avatars,
    displaySettings: store.displaySettings,
    isLoading,
    getAvatarUrl,
    getAvatarConfig,
    setAvatar,
    removeAvatar,
    updateDisplaySettings,
    hasCustomAvatar,
    exportAvatars,
    importAvatars,
  };
}

export default useAvatarStore;

