/**
 * Alden Command Center - Optimized Version
 * Refactored to use the shared UnifiedCommandCenter shell with performance optimizations.
 */
import React, { useEffect, useMemo, useRef, useState, useCallback, memo } from 'react';
import { Plus, Calendar, Mail, CheckCircle2, Clock, Settings, X, Lock, Unlock, ChevronLeft, ChevronRight, Mic } from 'lucide-react';
import { Rnd } from 'react-rnd';

import type { Task, CalendarEvent } from '../../types/agents';
import { AldenBridge } from '../../bridges/AldenBridge';
import { SettingsPanelEnhanced } from '../settings/SettingsPanelEnhanced';
import EmailManager from '../EmailManager';
import { GlobalCommsStream } from '../mythtech/tasks/GlobalCommsStream';
import { UnifiedCommandCenterOptimized as UnifiedCommandCenter, type UnifiedTabItem } from '../shared/premium/UnifiedCommandCenter.optimized';
import { CreateEventModal } from './CreateEventModal';
import { CreateTaskModal } from './CreateTaskModal';
import { TaskDetailModal } from './TaskDetailModal';
import { EventDetailModal } from './EventDetailModal';
import { CalendarView } from './CalendarView';
import { ChatView } from '../shared/chat';

export interface AldenCommandCenterProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
}

type ViewMode = 'overview' | 'tasks' | 'calendar' | 'email' | 'chat';
type DashboardPreset = 'standard' | 'mission' | 'schedule' | 'compact';
type DashboardTileId =
  | 'counter-tasks'
  | 'counter-schedule'
  | 'counter-inbox'
  | 'counter-uptime'
  | 'panel-mission'
  | 'panel-schedule';

const FRAME_PADDING = 16;
const TILE_GAP = 12;
const MIN_Y_BUFFER = 100;
const ROW_HEIGHT = 180;
const ROW_GAP = 20;
const ROW0_Y = FRAME_PADDING + MIN_Y_BUFFER;
const ROW1_Y = ROW0_Y + ROW_HEIGHT + ROW_GAP;

interface DashboardLayoutItem {
  id: DashboardTileId;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
}

const DEFAULT_DASHBOARD_LAYOUT: DashboardLayoutItem[] = [
  { id: 'counter-tasks', x: FRAME_PADDING, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-schedule', x: 240 + FRAME_PADDING * 2, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-inbox', x: 240 * 2 + FRAME_PADDING * 3, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'counter-uptime', x: 240 * 3 + FRAME_PADDING * 4, y: ROW0_Y, width: 240, height: 180, locked: true },
  { id: 'panel-mission', x: FRAME_PADDING, y: ROW1_Y, width: 600, height: 240, locked: true },
  { id: 'panel-schedule', x: 600 + FRAME_PADDING * 2 + TILE_GAP, y: ROW1_Y, width: 280, height: 240, locked: true },
];

// Helper function for layout packing
const packLayout = (items: DashboardLayoutItem[]): DashboardLayoutItem[] => {
  const counters = items.filter(i => i.id.startsWith('counter')).sort((a, b) => a.id.localeCompare(b.id));
  const panels = items.filter(i => !i.id.startsWith('counter')).sort((a, b) => a.id.localeCompare(b.id));

  let xCounter = FRAME_PADDING;
  const packedCounters = counters.map(item => {
    const packed = { ...item, x: xCounter, y: ROW0_Y };
    xCounter += item.width + TILE_GAP;
    return packed;
  });

  let xPanel = FRAME_PADDING;
  const packedPanels = panels.map(item => {
    const packed = { ...item, x: xPanel, y: ROW1_Y };
    xPanel += item.width + TILE_GAP;
    return packed;
  });

  const packed = [...packedCounters, ...packedPanels];
  return DEFAULT_DASHBOARD_LAYOUT.map(def => packed.find(i => i.id === def.id) || def);
};

// Memoized MetricCard component
const MetricCard = memo<{
  title: string;
  value: number;
  subtitle: string;
  accentColor: 'cyan' | 'gold' | 'green';
  icon: React.ReactNode;
  active?: boolean;
}>(({ title, value, subtitle, accentColor, icon, active }) => {
  // Memoized styles to prevent recreation
  const styles = useMemo(() => ({
    cyan: 'text-[#9fb4d6]',
    gold: 'text-alden-gold',
    green: 'text-alden-cyan',
  } as const), []);

  const activeStyle = useMemo(() => ({
    border: '1px solid rgba(0, 240, 255, 0.55)',
    boxShadow: '0 8px 20px rgba(0, 240, 255, 0.18), inset 0 0 0 1px rgba(197,160,89,0.38)',
    background: 'linear-gradient(135deg, rgba(0,240,255,0.12), rgba(0,240,255,0.03) 42%, rgba(197,160,89,0.06))',
  }), []);

  const inactiveStyle = useMemo(() => ({
    border: '1px solid rgba(120, 135, 170, 0.55)',
    boxShadow: '0 6px 14px rgba(0, 0, 0, 0.35)',
    background: 'linear-gradient(135deg, rgba(255,255,255,0.03), rgba(0,0,0,0.18) 45%, rgba(0,0,0,0.35))',
  }), []);

  const valueColor = useMemo(() => active
    ? 'text-[#e5f7ff]'
    : accentColor === 'gold'
      ? 'text-alden-gold'
      : accentColor === 'green'
        ? 'text-[#72f0ff]'
        : styles.cyan, [active, accentColor, styles]);

  const borderClasses = useMemo(() => active
    ? 'border ring-1 ring-[rgba(0,240,255,0.32)] bg-gradient-to-br from-[rgba(0,240,255,0.08)] via-transparent to-[rgba(0,0,0,0.25)]'
    : 'border ring-1 ring-[rgba(255,255,255,0.12)] hover:border-[rgba(0,240,255,0.35)]', [active]);

  return (
    <div
      className={`relative bg-alden-panel rounded-xl p-4 overflow-hidden transition-all duration-300 ${borderClasses} w-full h-full flex flex-col gap-2`}
      style={active ? activeStyle : inactiveStyle}
    >
      <div className="flex items-center gap-1.5 text-[#9fb4d6]">
        {icon}
      </div>
      <div className={`text-2xl font-bold tracking-tight font-orbitron-force ${valueColor}`}>{value}</div>
      <div className="text-[#e5c478] font-semibold text-[11px] uppercase tracking-[0.2em] font-orbitron-force leading-snug">{title}</div>
      <div className={`text-[11px] uppercase tracking-widest font-inter leading-snug ${active ? 'text-[#72f0ff] font-semibold' : 'text-[#9fb4d6]'}`}>
        {subtitle}
      </div>
      <div className="absolute bottom-0 right-0 w-8 h-8 border-b-2 border-r-2 rounded-br-xl opacity-30 border-alden-cyan" />
    </div>
  );
});

MetricCard.displayName = 'MetricCard';

