 *
 * - Column 1: AI Personas list (system + Mimic personas)
 * - Column 2: Agent Bridge - group chat with all active AI agents
 * - Column 3: Active Agent - avatar, status, and system metrics
 * - Column 4: Private Channel - 1-on-1 chat with module's resident AI
 */
import React, { memo, useState, useCallback, useMemo } from 'react';
import { PersonaRoster } from './PersonaRoster';
import { AgentBridge } from './AgentBridge';
import { ActiveAgentPanel } from './ActiveAgentPanel';
import { PrivateChannel } from './PrivateChannel';
import type {
  Persona,
  ChatMessage,
  SystemMetrics,
  PersonaStatus,
  SystemPersonaId,
  SYSTEM_PERSONAS,
} from './types';

export interface UnifiedChatLayoutProps {
  // Module context
  moduleId: string;
  userId: string;

  // Personas
  personas: Persona[];
  activePersonaId: string;
  residentPersonaId: SystemPersonaId;
  onPersonaSelect: (personaId: string) => void;

  // Group chat (Agent Bridge)
  groupMessages: ChatMessage[];
  groupLoading: boolean;
  groupError: string | null;
  groupTypingPersonas: string[];
  onSendGroupMessage: (content: string) => void;

  // Private chat
  privateMessages: ChatMessage[];
  privateLoading: boolean;
  privateError: string | null;
  privateTyping: boolean;
  onSendPrivateMessage: (content: string) => void;

  // System metrics
  metrics: SystemMetrics;

  // Layout options
  showGroupChat?: boolean;
  initialCollapsed?: boolean;

  // Navigation
  onClose?: () => void;
  className?: string;
}

export const UnifiedChatLayout = memo<UnifiedChatLayoutProps>(({
  moduleId,
  userId,
  personas,
  activePersonaId,
  residentPersonaId,
  onPersonaSelect,
  groupMessages,
  groupLoading,
  groupError,
  groupTypingPersonas,
  onSendGroupMessage,
  privateMessages,
  privateLoading,
  privateError,
  privateTyping,
  onSendPrivateMessage,
  metrics,
  showGroupChat = true,
  initialCollapsed = false,
  onClose,
  className = '',
}) => {
  const [isPrivateCollapsed, setIsPrivateCollapsed] = useState(initialCollapsed);

  // Get active persona object
  const activePersona = useMemo(() =>
    personas.find(p => p.id === activePersonaId) || personas[0],
    [personas, activePersonaId]
  );

  // Get resident persona object
  const residentPersona = useMemo(() =>
    personas.find(p => p.id === residentPersonaId) || personas[0],
    [personas, residentPersonaId]
  );

  // Handle persona selection
  const handlePersonaSelect = useCallback((personaId: string) => {
    onPersonaSelect(personaId);
  }, [onPersonaSelect]);

  // Toggle private channel collapse
  const togglePrivateCollapse = useCallback(() => {
    setIsPrivateCollapsed(prev => !prev);
  }, []);

  // Calculate grid columns based on collapsed state
  const gridCols = isPrivateCollapsed
    ? '200px 1fr 200px 50px'  // Collapsed private
    : '200px 1fr 200px 280px'; // Expanded
  
  return (
    <div 
      className={`h-full w-full bg-[#020617] text-[#c5d2e8] font-sans overflow-hidden relative ${className}`}
      style={{
        display: 'grid',
        gridTemplateColumns: gridCols
      }}
    >
      {/* Global glassmorphism overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-[#0b1628]/10 via-transparent to-[#0b1628]/10 pointer-events-none z-0" />

      {/* Column 1: Persona Roster */}
      <div className="border-r border-[#d6b369]/20 relative z-10">
        <PersonaRoster
          personas={personas}
          activePersonaId={activePersonaId}
          onPersonaSelect={handlePersonaSelect}
        />
      </div>

      {/* Column 2: Agent Bridge (Multi-Agent Chat) */}
      <div className="border-r border-[#d6b369]/20 relative z-10 min-w-0">
        {showGroupChat ? (
          <AgentBridge
            messages={groupMessages}
            personas={personas}
            isLoading={groupLoading}
            error={groupError}
            typingPersonas={groupTypingPersonas}
            onSendMessage={onSendGroupMessage}
          />
        ) : (
          // Fallback to private chat taking the main area
          <PrivateChannel
            residentPersona={residentPersona}
            messages={privateMessages}
            isLoading={privateLoading}
            error={privateError}
            isTyping={privateTyping}
            onSendMessage={onSendPrivateMessage}
            isCollapsed={false}
          />
        )}
      </div>

      {/* Column 3: Active Agent Panel */}
      <div className="border-r border-[#d6b369]/20 relative z-10">
        <ActiveAgentPanel
          persona={activePersona}
          metrics={metrics}
        />
      </div>

      {/* Column 4: Private Channel */}
      <div className="relative z-10">
        <PrivateChannel
          residentPersona={residentPersona}
          messages={privateMessages}
          isLoading={privateLoading}
          error={privateError}
          isTyping={privateTyping}
          onSendMessage={onSendPrivateMessage}
          isCollapsed={isPrivateCollapsed}
          onToggleCollapse={togglePrivateCollapse}
        />
      </div>
    </div>
  );
});

UnifiedChatLayout.displayName = 'UnifiedChatLayout';

export default UnifiedChatLayout;

