            return CliCommand::Unknown {
                input: format!("Commands must start with /. Type /help for usage."),
            };
        }

        let parts: Vec<&str> = input[1..].split_whitespace().collect();
        if parts.is_empty() {
            return CliCommand::Unknown {
                input: "Empty command".to_string(),
            };
        }

        let command = parts[0];

        match command {
            "task" => Self::parse_task_command(&parts[1..]),
            "event" => Self::parse_event_command(&parts[1..]),
            "help" => Self::parse_help_command(&parts[1..]),
            "status" => CliCommand::Status,
            "clear" => CliCommand::Clear,
            _ => CliCommand::Custom {
                name: command.to_string(),
                args: parts[1..].iter().map(|s| s.to_string()).collect(),
            },
        }
    }

