fn normalize_custom_command_name(input: &str) -> Result<String, String> {
    let trimmed = input.trim().trim_start_matches('/');
    if trimmed.is_empty() {
        return Err("Command name cannot be empty".to_string());
    }
    let normalized = trimmed.to_lowercase();
    let re = Regex::new(r"^[a-z0-9_\-]+$").unwrap();
    if !re.is_match(&normalized) {
        return Err(
            "Command name may only contain lowercase letters, numbers, underscores, or hyphens"
                .to_string(),
        );
    }
    Ok(normalized)
}

fn render_custom_response(template: &str, args: &[String]) -> String {
    let joined = if args.is_empty() {
        String::new()
    } else {
        args.join(" ")
    };

    template
        .replace("{args}", &joined)
        .replace("{arg1}", args.get(0).map(String::as_str).unwrap_or(""))
        .replace("{arg2}", args.get(1).map(String::as_str).unwrap_or(""))
        .replace("{arg3}", args.get(2).map(String::as_str).unwrap_or(""))
}

fn format_timestamp(ts: i64) -> String {
    Utc.timestamp_opt(ts, 0)
        .single()
        .unwrap_or_else(|| Utc::now())
        .to_rfc3339()
}

fn row_to_custom_command(row: SqliteRow) -> CustomCommand {
    let created_at: i64 = row.get("created_at");
    let updated_at: i64 = row.get("updated_at");
    CustomCommand {
        id: row.get("id"),
        user_id: row.get("user_id"),
        command: row.get("command"),
        description: row.get::<Option<String>, _>("description"),
        response_template: row.get("response_template"),
        created_at: format_timestamp(created_at),
        updated_at: format_timestamp(updated_at),
    }
}

