/// Send a chat message via AldenBridge
#[tauri::command]
pub async fn alden_chat_send(
    request: SendChatMessageRequest,
    state: State<'_, ChatState>,
) -> Result<ChatMessageEntry, String> {
    let trimmed = request.message.trim();
    if trimmed.is_empty() {
        return Err("Message cannot be empty".to_string());
    }

    let user_message =
        ChatMessageEntry::new(&request.user_id, "user", trimmed, &request.session_id);
    state.append_history(&request.user_id, user_message).await;

    let response_text = state
        .chat_manager
        .handle_message(HandleMessageRequest {
            user_id: request.user_id.clone(),
            message: trimmed.to_string(),
            session_id: request.session_id.clone(),
        })
        .await?;

    let response_entry = ChatMessageEntry::new(
        &request.user_id,
        "alden",
        &response_text,
        &request.session_id,
    );
    state
        .append_history(&request.user_id, response_entry.clone())
        .await;

    Ok(response_entry)
}

