async fn handle_task_list(
        &self,
        user_id: &str,
        status: Option<String>,
        task_manager: Option<Arc<RwLock<TaskManager>>>,
    ) -> CliResponse {
        let manager = match task_manager {
            Some(m) => m,
            None => {
                return CliResponse {
                    success: false,
                    output: "Task manager not initialized".to_string(),
                    data: None,
                }
            }
        };

        // Build filter based on status
        let filter = TaskFilter {
            user_id: Some(user_id.to_string()),
            status: status.clone(),
            ..Default::default()
        };

        match manager.read().await.list_tasks(filter).await {
            Ok(tasks) => {
                if tasks.is_empty() {
                    return CliResponse {
                        success: true,
                        output: r#"
\U0001f4cb ACTIVE TASKS
────────────────────────────────────────────────────
 ID    │ Priority │ Due Date   │ Title
────────────────────────────────────────────────────
 (No tasks yet - use /task create to add one)

\U0001f4a1 Tip: Use /task create "Your task" to get started!
                        "#
                        .to_string(),
                        data: Some(json!(tasks)),
                    };
                }

                let mut output = String::from("\n\U0001f4cb ACTIVE TASKS\n");
                output.push_str("────────────────────────────────────────────────────\n");
                output.push_str(" ID    │ Priority │ Due Date   │ Title\n");
                output.push_str("────────────────────────────────────────────────────\n");

                for task in &tasks {
                    let id_short = &task.id[..8.min(task.id.len())];
                    let priority_icon = match task.priority.as_str() {
                        "critical" => "\U0001f534",
                        "high" => "\U0001f7e0",
                        "medium" => "\U0001f7e1",
                        _ => "⚪",
                    };
                    let due_str = format_optional_ts(task.due_date.clone());

                    output.push_str(&format!(
                        " {:<5} │ {:<8} │ {:<10} │ {}\n",
                        id_short, priority_icon, due_str, task.title
                    ));
                }

                output.push_str(&format!("\nTotal: {} tasks\n", tasks.len()));

                CliResponse {
                    success: true,
                    output,
                    data: Some(json!(tasks)),
                }
            }
            Err(e) => CliResponse {
                success: false,
                output: format!("Failed to list tasks: {}", e),
                data: None,
            },
        }
    }

    async fn handle_task_complete(
        &self,
        user_id: &str,
        task_id: String,
        task_manager: Option<Arc<RwLock<TaskManager>>>,
    ) -> CliResponse {
        let manager = match task_manager {
            Some(m) => m,
            None => {
                return CliResponse {
                    success: false,
                    output: "Task manager not initialized".to_string(),
                    data: None,
