        match manager.read().await.update_task(request).await {
            Ok(task) => CliResponse {
                success: true,
                output: format!("✅ Task completed: \"{}\"", task.title),
                data: Some(json!(task)),
            },
            Err(e) => CliResponse {
                success: false,
                output: format!("Failed to complete task: {}", e),
                data: None,
            },
        }
    }

    async fn handle_task_delete(
        &self,
