pub async fn alden_cli_execute(
    request: ExecuteCommandRequest,
    state: State<'_, CliState>,
) -> Result<CliResponse, String> {
    let command = CliParser::parse(&request.command);

    // Pass task_manager and calendar_manager from state
    let response = state
        .executor
        .execute(
            command,
            &request.user_id,
            state.task_manager.clone(),
            state.calendar_manager.clone(),
        )
        .await;

    let now = Utc::now();
    let entry = CommandHistoryEntry {
        command: request.command.clone(),
        executed_at: now.to_rfc3339(),
        success: response.success,
    };

    // Persist history (to both memory and DB)
    let _ = state.append_history(&request.user_id, entry, now.timestamp()).await;

    Ok(response)
}

