#[tauri::command]
pub async fn synapse_create_connection(
    request: CreateConnectionRequest,
    state: State<'_, Arc<RwLock<SynapseState>>>,
) -> Result<SynapseConnection, String> {
    let state = state.read().await;

    // Check network permission before creating connection
    if !state.security.check_network_permission(&request.plugin_id, &request.endpoint).await? {
        return Err(format!("Plugin does not have permission to access {}", request.endpoint));
