pub struct SynapseManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl SynapseManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Create Synapse tables
        let queries = vec![
            // Plugins table
            r#"
                CREATE TABLE IF NOT EXISTS synapse_plugins (
                    plugin_id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    version TEXT NOT NULL,
                    description TEXT NOT NULL,
                    permissions TEXT NOT NULL, -- JSON
                    enabled BOOLEAN DEFAULT TRUE,
                    manifest TEXT NOT NULL, -- JSON
                    created_at TEXT NOT NULL
                )
            "#,
            // Connections table
            r#"
                CREATE TABLE IF NOT EXISTS synapse_connections (
                    connection_id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    endpoint TEXT NOT NULL,
                    credentials TEXT NOT NULL, -- JSON
                    status TEXT NOT NULL,
                    last_activity TEXT NOT NULL
                )
            "#,
            // Security logs table
            r#"
                CREATE TABLE IF NOT EXISTS synapse_security_logs (
                    log_id TEXT PRIMARY KEY,
                    connection_id TEXT,
                    event_type TEXT NOT NULL,
                    severity TEXT NOT NULL,
                    timestamp TEXT NOT NULL,
                    details TEXT NOT NULL -- JSON
                )
            "#,
        ];

        for query in queries {
            sqlx::query(query)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to create Synapse table: {}", e))?;
        }

        Ok(())
    }
}

