/// Get security logs
#[tauri::command]
pub async fn synapse_get_security_logs(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    connection_id: Option<String>,
    event_type: Option<String>,
    severity: Option<String>,
    limit: Option<i64>,
) -> Result<Vec<SynapseSecurityLog>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Build query with optional filters
    let mut query_builder = sqlx::QueryBuilder::new(
        "SELECT log_id, connection_id, event_type, severity, timestamp, details FROM synapse_security_logs WHERE 1=1"
    );

    if let Some(cid) = &connection_id {
        query_builder.push(" AND connection_id = ");
        query_builder.push_bind(cid);
    }

    if let Some(et) = &event_type {
        query_builder.push(" AND event_type = ");
        query_builder.push_bind(et);
    }

    if let Some(s) = &severity {
        query_builder.push(" AND severity = ");
        query_builder.push_bind(s);
    }

    query_builder.push(" ORDER BY timestamp DESC");

    if let Some(l) = limit {
        query_builder.push(" LIMIT ");
        query_builder.push_bind(l);
    }

    let rows = query_builder
        .build_query_as::<SynapseSecurityLogRow>()
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch Synapse security logs: {}", e))?;

    let logs = rows.into_iter().map(|row| row.into()).collect();
    Ok(logs)
}

