    plugin_id: String,
    permissions: Vec<String>,
) -> Result<SynapsePlugin, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let permissions_json = serde_json::to_string(&permissions).map_err(|e| e.to_string())?;

    let query = r#"
        UPDATE synapse_plugins
        SET permissions = ?
        WHERE plugin_id = ?
    "#;

    sqlx::query(query)
        .bind(&permissions_json)
        .bind(&plugin_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to update plugin permissions: {}", e))?;

    // Get updated plugin
    let get_query = r#"
        SELECT plugin_id, name, version, description, permissions, enabled, manifest, created_at
        FROM synapse_plugins
        WHERE plugin_id = ?
    "#;

    let row = sqlx::query_as::<_, SynapsePluginRow>(get_query)
        .bind(&plugin_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch updated plugin: {}", e))?;

    Ok(row.into())
}

