/// Test connection (basic connectivity check)
#[tauri::command]
pub async fn synapse_test_connection(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    connection_id: String,
) -> Result<HashMap<String, serde_json::Value>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    // Get connection details
    let query = r#"
        SELECT connection_id, name, endpoint, credentials, status, last_activity
        FROM synapse_connections
        WHERE connection_id = ?
    "#;

    let row = sqlx::query_as::<_, SynapseConnectionRow>(query)
        .bind(&connection_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch connection: {}", e))?;

    let endpoint = row.endpoint.clone();

    // Basic connectivity test (ping-like check)
    let start = std::time::Instant::now();
    let test_result = match reqwest::get(&endpoint).await {
        Ok(response) => {
            let latency = start.elapsed().as_millis() as u64;
            let mut result = HashMap::new();
            result.insert("success".to_string(), serde_json::Value::Bool(true));
            result.insert(
                "status_code".to_string(),
                serde_json::Value::Number(response.status().as_u16().into()),
            );
            result.insert(
                "latency_ms".to_string(),
                serde_json::Value::Number(latency.into()),
            );
            result.insert(
                "endpoint".to_string(),
                serde_json::Value::String(endpoint.clone()),
            );
            result
        }
        Err(e) => {
            let mut result = HashMap::new();
            result.insert("success".to_string(), serde_json::Value::Bool(false));
            result.insert(
                "error".to_string(),
                serde_json::Value::String(e.to_string()),
            );
            result.insert(
                "endpoint".to_string(),
                serde_json::Value::String(endpoint.clone()),
            );
            result
        }
    };

    // Update connection status based on test
    let new_status = if test_result
        .get("success")
        .and_then(|v| v.as_bool())
        .unwrap_or(false)
    {
        "connected"
    } else {
        "failed"
    };

    let update_query = r#"
        UPDATE synapse_connections
        SET status = ?, last_activity = ?
        WHERE connection_id = ?
    "#;

    let last_activity = chrono::Utc::now().to_rfc3339();
    sqlx::query(update_query)
        .bind(new_status)
        .bind(&last_activity)
        .bind(&connection_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to update connection status: {}", e))?;

    Ok(test_result)
}

