/// Plugin Lifecycle Management for Synapse
///
/// Manages plugin installation, enabling/disabling, updates, and state transitions
use crate::db_manager::DatabaseManager;
use crate::synapse::models::*;
use crate::synapse::security::SecurityBoundary;
use std::sync::Arc;
use tokio::sync::RwLock;

pub struct PluginManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
    security: Arc<SecurityBoundary>,
}

impl PluginManager {
    pub fn new(
        db: Arc<RwLock<Option<DatabaseManager>>>,
        security: Arc<SecurityBoundary>,
    ) -> Self {
        Self { db, security }
    }

    /// Initialize database schema for plugins
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            CREATE TABLE IF NOT EXISTS synapse_plugins (
                plugin_id TEXT PRIMARY KEY NOT NULL,
                name TEXT NOT NULL,
                version TEXT NOT NULL,
                description TEXT NOT NULL,
                permissions TEXT NOT NULL,
                enabled INTEGER NOT NULL DEFAULT 0,
                state TEXT NOT NULL DEFAULT 'installed',
                manifest TEXT NOT NULL,
                created_at TEXT NOT NULL,
                updated_at TEXT
            )
        "#;

        sqlx::query(query)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to create synapse_plugins table: {}", e))?;

        Ok(())
    }

    /// Install a new plugin from manifest
    pub async fn install_plugin(
        &self,
        manifest: PluginManifest,
    ) -> Result<SynapsePlugin, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Validate manifest
        self.validate_manifest(&manifest)?;

        let plugin_id = manifest.plugin_id.clone();
        let created_at = chrono::Utc::now().to_rfc3339();

        // Extract permissions from manifest
        let permissions = manifest.permissions.read.iter()
            .chain(manifest.permissions.write.iter())
            .chain(manifest.permissions.network.iter())
            .cloned()
            .collect::<Vec<_>>();

        let permissions_json = serde_json::to_string(&permissions)
            .map_err(|e| format!("Failed to serialize permissions: {}", e))?;

        let manifest_json = serde_json::to_string(&manifest)
            .map_err(|e| format!("Failed to serialize manifest: {}", e))?;

        let state_json = serde_json::to_string(&PluginState::Installed)
            .map_err(|e| format!("Failed to serialize state: {}", e))?;

        let query = r#"
            INSERT INTO synapse_plugins (
                plugin_id, name, version, description, permissions,
                enabled, state, manifest, created_at
            ) VALUES (?, ?, ?, ?, ?, 0, ?, ?, ?)
        "#;

        sqlx::query(query)
            .bind(&plugin_id)
            .bind(&manifest.name)
            .bind(&manifest.version)
            .bind(&manifest.description)
            .bind(&permissions_json)
            .bind(&state_json)
            .bind(&manifest_json)
            .bind(&created_at)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to install plugin: {}", e))?;

        // Log security event
        self.security
            .log_event(
                Some(plugin_id.clone()),
                None,
                SecurityEventType::PluginInstalled,
                SecuritySeverity::Info,
                format!("Plugin {} v{} installed", manifest.name, manifest.version),
            )
            .await?;

        Ok(SynapsePlugin {
            plugin_id,
            name: manifest.name,
            version: manifest.version,
            description: manifest.description,
            permissions,
            enabled: false,
            state: PluginState::Installed,
            manifest,
            created_at,
        })
    }

    /// Enable a plugin (transition from Installed/Disabled -> Enabled)
    pub async fn enable_plugin(&self, plugin_id: &str) -> Result<SynapsePlugin, String> {
        let plugin = self.get_plugin(plugin_id).await?;

        // Validate state transition
        match plugin.state {
            PluginState::Installed | PluginState::Disabled => {
                // Valid transition
            }
            PluginState::Enabled => {
                return Err("Plugin is already enabled".to_string());
            }
            PluginState::Error(_) => {
                return Err("Cannot enable plugin in error state".to_string());
            }
            PluginState::Updating => {
                return Err("Plugin is currently updating".to_string());
            }
        }

        // Update state
        self.update_plugin_state(plugin_id, PluginState::Enabled, true)
            .await?;

        // Log security event
        self.security
            .log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::PluginEnabled,
                SecuritySeverity::Info,
                format!("Plugin {} enabled", plugin.name),
            )
            .await?;

        self.get_plugin(plugin_id).await
    }

    /// Disable a plugin (transition from Enabled -> Disabled)
    pub async fn disable_plugin(&self, plugin_id: &str) -> Result<SynapsePlugin, String> {
        let plugin = self.get_plugin(plugin_id).await?;

        // Validate state transition
        if !matches!(plugin.state, PluginState::Enabled) {
            return Err("Plugin is not enabled".to_string());
        }

        // Update state
        self.update_plugin_state(plugin_id, PluginState::Disabled, false)
            .await?;

        // Log security event
        self.security
            .log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::PluginDisabled,
                SecuritySeverity::Info,
                format!("Plugin {} disabled", plugin.name),
            )
            .await?;

        self.get_plugin(plugin_id).await
    }

    /// Get a single plugin by ID
    pub async fn get_plugin(&self, plugin_id: &str) -> Result<SynapsePlugin, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"
            SELECT plugin_id, name, version, description, permissions,
                   enabled, state, manifest, created_at
            FROM synapse_plugins
            WHERE plugin_id = ?
        "#;

        let row = sqlx::query_as::<_, SynapsePluginRow>(query)
            .bind(plugin_id)
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Plugin not found: {}", e))?;

        Ok(row.into())
    }

    /// List all plugins with optional filter by state
    pub async fn list_plugins(
        &self,
        enabled_only: bool,
    ) -> Result<Vec<SynapsePlugin>, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = if enabled_only {
            r#"
                SELECT plugin_id, name, version, description, permissions,
                       enabled, state, manifest, created_at
                FROM synapse_plugins
                WHERE enabled = 1
                ORDER BY created_at DESC
            "#
        } else {
            r#"
                SELECT plugin_id, name, version, description, permissions,
                       enabled, state, manifest, created_at
                FROM synapse_plugins
                ORDER BY created_at DESC
            "#
        };

        let rows = sqlx::query_as::<_, SynapsePluginRow>(query)
            .fetch_all(&db.pool)
            .await
            .map_err(|e| format!("Failed to list plugins: {}", e))?;

        Ok(rows.into_iter().map(|row| row.into()).collect())
    }

    /// Update plugin state
    async fn update_plugin_state(
        &self,
        plugin_id: &str,
        state: PluginState,
        enabled: bool,
    ) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let state_json = serde_json::to_string(&state)
            .map_err(|e| format!("Failed to serialize state: {}", e))?;

        let updated_at = chrono::Utc::now().to_rfc3339();

        let query = r#"
            UPDATE synapse_plugins
            SET state = ?, enabled = ?, updated_at = ?
            WHERE plugin_id = ?
        "#;

        sqlx::query(query)
            .bind(&state_json)
            .bind(enabled as i32)
            .bind(&updated_at)
            .bind(plugin_id)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to update plugin state: {}", e))?;

        Ok(())
    }

    /// Delete/uninstall a plugin
    pub async fn uninstall_plugin(&self, plugin_id: &str) -> Result<(), String> {
        let plugin = self.get_plugin(plugin_id).await?;

        // Can only uninstall disabled plugins
        if matches!(plugin.state, PluginState::Enabled) {
            return Err("Cannot uninstall enabled plugin. Disable it first.".to_string());
        }

        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let query = r#"DELETE FROM synapse_plugins WHERE plugin_id = ?"#;

        sqlx::query(query)
            .bind(plugin_id)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to uninstall plugin: {}", e))?;

        Ok(())
    }

    /// Update plugin permissions
    pub async fn update_permissions(
        &self,
        plugin_id: &str,
        permissions: Vec<String>,
    ) -> Result<SynapsePlugin, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        let permissions_json = serde_json::to_string(&permissions)
            .map_err(|e| format!("Failed to serialize permissions: {}", e))?;

        let query = r#"
            UPDATE synapse_plugins
            SET permissions = ?
            WHERE plugin_id = ?
        "#;

        sqlx::query(query)
            .bind(&permissions_json)
            .bind(plugin_id)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to update permissions: {}", e))?;

        self.get_plugin(plugin_id).await
    }

    /// Validate plugin manifest
    fn validate_manifest(&self, manifest: &PluginManifest) -> Result<(), String> {
        // Check required fields
        if manifest.plugin_id.is_empty() {
            return Err("Plugin ID is required".to_string());
        }

        if manifest.name.is_empty() {
            return Err("Plugin name is required".to_string());
        }

        if manifest.version.is_empty() {
            return Err("Plugin version is required".to_string());
        }

        // Validate plugin_id format (alphanumeric + underscores only)
        if !manifest
            .plugin_id
            .chars()
            .all(|c| c.is_alphanumeric() || c == '_')
        {
            return Err("Plugin ID must contain only alphanumeric characters and underscores".to_string());
        }

        // Validate permissions
        for perm in &manifest.permissions.network {
            if !perm.starts_with("http://") && !perm.starts_with("https://") {
                return Err(format!("Invalid network permission: {}", perm));
            }
        }

        Ok(())
    }

    /// Mark plugin as error state
    pub async fn mark_plugin_error(
        &self,
        plugin_id: &str,
        error_message: String,
    ) -> Result<(), String> {
        self.update_plugin_state(plugin_id, PluginState::Error(error_message), false)
            .await
    }
}

