    /// Check if a plugin has permission for a specific resource
    pub async fn check_permission(
        &self,
        plugin_id: &str,
        resource: &str,
        action: &str, // "read" or "write"
    ) -> Result<bool, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Get plugin permissions
        let query = r#"
            SELECT permissions
            FROM synapse_plugins
            WHERE plugin_id = ? AND enabled = 1
        "#;

        let (permissions_json,): (String,) = sqlx::query_as(query)
            .bind(plugin_id)
            .fetch_one(&db.pool)
            .await
            .map_err(|_| format!("Plugin {} not found or not enabled", plugin_id))?;

        let permissions: Vec<String> = serde_json::from_str(&permissions_json)
            .map_err(|e| format!("Failed to parse permissions: {}", e))?;

        // Check if permission exists
        let required_perm = format!("{}:{}", action, resource);
        let has_permission = permissions.iter().any(|p| p == &required_perm || p == "*");

        if !has_permission {
            // Log permission denial
            self.log_event(
                Some(plugin_id.to_string()),
                None,
                SecurityEventType::PermissionDenied,
                SecuritySeverity::Warning,
                format!("Permission denied: {} on {}", action, resource),
            )
            .await?;
        }

        Ok(has_permission)
    }

