                log_id, plugin_id, connection_id, event_type,
                severity, timestamp, details
            ) VALUES (?, ?, ?, ?, ?, ?, ?)
        "#;

        sqlx::query(query)
            .bind(&log_id)
            .bind(&plugin_id)
            .bind(&connection_id)
            .bind(&event_type_json)
            .bind(&severity_json)
            .bind(&timestamp)
            .bind(&details_json)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to log security event: {}", e))?;

        Ok(log_id)
    }

