    /// Get security summary statistics
    pub async fn get_security_summary(&self) -> Result<SecuritySummary, String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Total logs
        let total_query = r#"SELECT COUNT(*) FROM synapse_security_logs"#;
        let (total_logs,): (i64,) = sqlx::query_as(total_query)
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Failed to count logs: {}", e))?;

        // Critical logs in last 24 hours
        let critical_query = r#"
            SELECT COUNT(*)
            FROM synapse_security_logs
            WHERE severity = ? AND timestamp > datetime('now', '-1 day')
        "#;
        let severity_json = serde_json::to_string(&SecuritySeverity::Critical).unwrap();
        let (critical_24h,): (i64,) = sqlx::query_as(critical_query)
            .bind(&severity_json)
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Failed to count critical logs: {}", e))?;

        // Count by severity
        let severity_query = r#"
            SELECT severity, COUNT(*) as count
            FROM synapse_security_logs
            GROUP BY severity
        "#;
        let severity_rows: Vec<(String, i64)> = sqlx::query_as(severity_query)
            .fetch_all(&db.pool)
            .await
            .map_err(|e| format!("Failed to count by severity: {}", e))?;

        let mut by_severity = HashMap::new();
        for (severity_json, count) in severity_rows {
            if let Ok(severity) = serde_json::from_str::<SecuritySeverity>(&severity_json) {
                by_severity.insert(format!("{:?}", severity).to_lowercase(), count);
            }
        }

        Ok(SecuritySummary {
            total_logs,
            critical_last_24h: critical_24h,
            by_severity,
        })
    }

