/// Simple token bucket rate limiter
struct RateLimiter {
    buckets: HashMap<String, TokenBucket>,
}

impl RateLimiter {
    fn new() -> Self {
        Self {
            buckets: HashMap::new(),
        }
    }

    /// Check if request is allowed (100 requests per minute per plugin)
    fn check_limit(&mut self, plugin_id: &str) -> bool {
        let bucket = self
            .buckets
            .entry(plugin_id.to_string())
            .or_insert_with(|| TokenBucket::new(100, 60));

        bucket.consume()
    }
}

struct TokenBucket {
    capacity: u32,
    tokens: u32,
    refill_rate: u32, // tokens per second
    last_refill: std::time::Instant,
}

impl TokenBucket {
    fn new(capacity: u32, refill_interval_secs: u64) -> Self {
        Self {
            capacity,
            tokens: capacity,
            refill_rate: capacity / refill_interval_secs as u32,
            last_refill: std::time::Instant::now(),
        }
    }

    fn consume(&mut self) -> bool {
        self.refill();

        if self.tokens > 0 {
            self.tokens -= 1;
            true
        } else {
            false
        }
    }

    fn refill(&mut self) {
        let now = std::time::Instant::now();
        let elapsed = now.duration_since(self.last_refill).as_secs();

        if elapsed > 0 {
            let new_tokens = (elapsed as u32 * self.refill_rate).min(self.capacity - self.tokens);
            self.tokens += new_tokens;
            self.last_refill = now;
        }
    }
}

