  /**
   * Search memories using semantic search with query text
   * This method uses the Python RAG script for semantic relevance calculation
   *
   * @param userId - User ID for filtering
   * @param queryText - Natural language search query
   * @param limit - Maximum number of results (default: 10)
   * @param minSimilarity - Minimum similarity score 0-1 (default: 0.6)
   * @param personaId - Optional persona filter
   * @param contentTypes - Optional content type filter
   * @returns Array of memory slices with similarity scores
   */
  static async searchMemoriesSemantic(
    userId: string,
    queryText: string,
    limit?: number,
    minSimilarity?: number,
    personaId?: string,
    contentTypes?: string[]
  ): Promise<BridgeResponse<(MemorySlice & { similarity_score?: number })[]>> {
    try {
      // Get all memories for the user (we'll rank them using RAG)
      const memoriesResponse = await this.getMemories(userId, {
        persona_id: personaId,
        content_type: contentTypes?.[0],
        status: 'active'
      });

      if (!memoriesResponse.success) {
        return memoriesResponse;
      }

      const memories = memoriesResponse.data;
      if (memories.length === 0) {
        return { success: true, data: [] };
      }

      // Extract content texts for RAG analysis
      const contentTexts = memories.map(m => m.content);

      try {
        // Use Python RAG script for semantic relevance calculation
        const relevanceResponse = await this.calculateRelevance(queryText, contentTexts);

        if (relevanceResponse.success) {
          const { scores, ranked_indices } = relevanceResponse.data;

          // Map memories with their relevance scores
          const memoriesWithScores = memories.map((memory, index) => ({
            ...memory,
            similarity_score: scores[index] || 0
          }));

          // Sort by ranked indices (already sorted by relevance)
          const sortedMemories = ranked_indices
            .map(idx => memoriesWithScores[idx])
            .filter(m => (m.similarity_score ?? 0) >= (minSimilarity ?? 0));

          // Apply limit
          const limitedMemories = sortedMemories.slice(0, limit || 10);

          return { success: true, data: limitedMemories };
        } else {
          // Fallback to simple text similarity if RAG fails
          console.warn('[VaultBridge] RAG calculation failed, using text similarity fallback');
          const memoriesWithScores = memories.map(memory => ({
            ...memory,
            similarity_score: this.calculateTextSimilarity(queryText, memory.content)
          }));

          const filteredMemories = minSimilarity
            ? memoriesWithScores.filter(m => (m.similarity_score ?? 0) >= minSimilarity)
            : memoriesWithScores;

          filteredMemories.sort((a, b) => (b.similarity_score ?? 0) - (a.similarity_score ?? 0));

          return { success: true, data: filteredMemories.slice(0, limit || 10) };
        }
      } catch (ragError) {
        // Fallback to simple text similarity if RAG throws an error
        console.warn('[VaultBridge] RAG calculation error, using text similarity fallback:', ragError);
        const memoriesWithScores = memories.map(memory => ({
          ...memory,
          similarity_score: this.calculateTextSimilarity(queryText, memory.content)
        }));

        const filteredMemories = minSimilarity
          ? memoriesWithScores.filter(m => (m.similarity_score ?? 0) >= minSimilarity)
          : memoriesWithScores;

        filteredMemories.sort((a, b) => (b.similarity_score ?? 0) - (a.similarity_score ?? 0));

        return { success: true, data: filteredMemories.slice(0, limit || 10) };
      }

    } catch (error) {
      console.error('[VaultBridge] Semantic search failed:', error);
      return { success: false, error: String(error) };
    }
  }

  /**
   * Calculate basic text similarity (0-1) as placeholder for actual embeddings
   * This is a temporary solution until embedding generation is integrated
   */
  private static calculateTextSimilarity(query: string, content: string): number {
    const queryWords = query.toLowerCase().split(/\s+/);
    const contentLower = content.toLowerCase();

    let matchCount = 0;
    for (const word of queryWords) {
      if (contentLower.includes(word)) {
        matchCount++;
      }
    }

    const similarity = matchCount / Math.max(queryWords.length, 1);
    return Math.min(similarity, 1.0);
  }
}

