// Prevents additional console window on Windows in release, DO NOT REMOVE!!
#![cfg_attr(not(debug_assertions), windows_subsystem = "windows")]

use std::collections::HashMap;
use std::env;
use std::sync::Arc;

use tauri::Manager;

mod alden; // Real Rust implementation (tasks, calendar, email)
mod alden_legacy; // Legacy Python IPC bridge
mod alice; // Behavioral Analysis and Insights
mod auth;
mod core; // Core orchestration system (sessions, messages, routing)
mod db_manager;
mod first_run;
mod intelligence;
mod key_manager;
mod llm_setup;
mod mimic;
mod secure_vault;
mod sentry;
mod sentry_auth; // Sentry authentication (JWT, sessions)
mod sentry_enhanced; // Enhanced Sentry with security events, compliance, and audit reports
mod settings;
#[path = "synapse_legacy_impl.rs"]
mod synapse_legacy; // Legacy Python IPC synapse
mod synapse; // New modular Synapse Integration Gateway
mod synapse_enhanced; // Enhanced Synapse with database-backed plugin/connection management
mod system_metrics;
mod vault; // Advanced Memory System (Memory Slices, RAG, KG, Context)
mod vault_rotation; // Settings persistence

// Import real Alden module components
use alden::calendar::*;
use alden::chat::*;
use alden::cli::*;
use alden::email::*;
use alden::feed::*;
// use alden::email::*; // Temporarily disabled
use alden::tasks::*;

// Import Vault module components
use vault::context_engine::*;
use vault::knowledge_graph::*;
use vault::memory_slices::*;
use vault::rag_engine::*;

// Import Settings commands

#[tauri::command]
fn get_app_status() -> Result<String, String> {
    Ok("Hearthlink Native App Running".to_string())
}

#[tauri::command]
fn greet(name: &str) -> String {
    format!("Hello, {}! Welcome to Hearthlink Native.", name)
}

fn main() {
    // SECURITY: Initialize key manager and authenticator
    let key_manager = Arc::new(key_manager::SecureKeyManager::new());
    let authenticator = Arc::new(auth::SecureAuthenticator::new(key_manager.clone()));

    // Initialize secure vault with security components
    let secure_vault = secure_vault::SecureVault::new(key_manager.clone(), authenticator.clone());

    let key_manager_for_setup = key_manager.clone();
    let authenticator_for_setup = authenticator.clone();

    // Note: Using cfg attributes directly with .invoke_handler() for better type inference
    #[cfg(debug_assertions)]
    let invoke_handler: Box<dyn Fn(tauri::Invoke) + Send + Sync> =
        Box::new(include!("invoke_handler_debug.inc"));

    #[cfg(not(debug_assertions))]
    let invoke_handler: Box<dyn Fn(tauri::Invoke) + Send + Sync> =
        Box::new(include!("invoke_handler_release.inc"));

    tauri::Builder::default()
        .manage(key_manager.clone())
        .manage(authenticator.clone())
        .manage(secure_vault)
        .setup(move |app| {
            let window = app.get_window("main").unwrap();
            let resource_dir = app
                .path_resolver()
                .resource_dir()
                .expect("Failed to get resource directory");
            println!("Resource directory: {}", resource_dir.display());

            // Initialize Settings Manager
            let settings_manager = settings::SettingsManager::new(&app.handle());
            let settings_state = settings::SettingsState {
                manager: std::sync::Mutex::new(settings_manager),
            };
            app.manage(settings_state);

            // Initialize database-backed managers before commands can run
            let key_manager = key_manager_for_setup.clone();
            let authenticator = authenticator_for_setup.clone();
            let app_handle = app.handle();
            let handle_for_init = app_handle.clone();
            tauri::async_runtime::block_on(async move {
                match db_manager::DatabaseManager::initialize(&handle_for_init).await {
                    Ok(db) => {
                        use tokio::sync::RwLock;
                        let db_arc = Arc::new(RwLock::new(Some(db)));

                        // Initialize Task Manager
                        let task_manager = Arc::new(RwLock::new(alden::tasks::TaskManager::new(
                            db_arc.clone(),
                        )));
                        if let Err(e) = task_manager.read().await.initialize_schema().await {
                            eprintln!("[Alden] Failed to initialize task schema: {}", e);
                        }
                        let alden_state = AldenState {
                            task_manager: task_manager.clone(),
                        };
                        handle_for_init.manage(alden_state);

                        // Initialize Calendar Manager
                        let calendar_manager = Arc::new(RwLock::new(
                            alden::calendar::CalendarManager::new(db_arc.clone()),
                        ));
                        if let Err(e) = calendar_manager.read().await.initialize_schema().await {
                            eprintln!("[Alden] Failed to initialize calendar schema: {}", e);
                        }
                        let calendar_state = CalendarState {
                            calendar_manager: calendar_manager.clone(),
                        };
                        handle_for_init.manage(calendar_state);

                        // Initialize Chat Manager (Phase 2 - Ollama LLM)
                        let chat_manager = alden::chat::ChatManager::new();
                        if let Some(db_ref) = db_arc.read().await.as_ref() {
                            if let Err(e) = alden::chat::initialize_schema(db_ref).await {
                                eprintln!("[Alden Chat] Failed to initialize chat schema: {}", e);
                            }
                        }
                        let chat_state = ChatState {
                            chat_manager: Arc::new(chat_manager),
                            history: Arc::new(RwLock::new(HashMap::new())),
                            typing_status: Arc::new(RwLock::new(HashMap::new())),
                            db: db_arc.clone(),
                        };
                        handle_for_init.manage(chat_state);

                        // Initialize CLI Executor (Phase 3 - Terminal)
                        let cli_executor = alden::cli::CliExecutor::new(db_arc.clone());
                        if let Err(e) = cli_executor.initialize_schema().await {
                            eprintln!(
                                "[Alden CLI] Failed to initialize custom command schema: {}",
                                e
                            );
                        }
                        let cli_state = CliState {
                            executor: Arc::new(cli_executor),
                            history: Arc::new(RwLock::new(HashMap::new())),
                            db: db_arc.clone(),
                            task_manager: Some(task_manager.clone()),
                            calendar_manager: Some(calendar_manager.clone()),
                        };
                        handle_for_init.manage(cli_state);

                        // Initialize Email Manager
                        /*
                        let mut email_manager = alden::email::EmailManager::new(db_arc.clone());
                        if let Err(e) = email_manager.initialize_schema().await {
                            eprintln!("[Alden Email] Failed to initialize email schema: {}", e);
                        }
                        email_manager.set_key_manager(key_manager.clone());
                        let email_state = EmailState {
                            email_manager: Arc::new(RwLock::new(email_manager)),
                        };
                        handle_for_init.manage(email_state);
                        */

                        // Initialize Vault Managers
                        let memory_manager = vault::memory_slices::MemorySliceManager::new(
                            db_arc.clone(),
                            key_manager.clone(),
                        );
                        if let Err(e) = memory_manager.initialize_schema().await {
                            eprintln!("[Vault] Failed to initialize memory schema: {}", e);
                        }

                        let rag_engine = vault::rag_engine::RagEngine::new(
                            db_arc.clone(),
                            key_manager.clone(),
                        );
                        if let Err(e) = rag_engine.initialize_schema().await {
                            eprintln!("[Vault] Failed to initialize RAG schema: {}", e);
                        }

                        let knowledge_graph =
                            vault::knowledge_graph::KnowledgeGraph::new(db_arc.clone());
                        if let Err(e) = knowledge_graph.initialize_schema().await {
                            eprintln!("[Vault] Failed to initialize Knowledge Graph schema: {}", e);
                        }

                        let context_engine =
                            vault::context_engine::ContextEngine::new(db_arc.clone());
                        if let Err(e) = context_engine.initialize_schema().await {
                            eprintln!("[Vault] Failed to initialize Context Engine schema: {}", e);
                        }

                        // Initialize Mimic Manager
                        let mimic_manager = mimic::MimicManager::new(db_arc.clone());
                        if let Err(e) = mimic_manager.initialize_schema().await {
                            eprintln!("[Mimic] Failed to initialize Mimic schema: {}", e);
                        }

                        // Initialize Alice Manager
                        let alice_manager = alice::AliceManager::new(db_arc.clone());
                        if let Err(e) = alice_manager.initialize_schema().await {
                            eprintln!("[Alice] Failed to initialize Alice schema: {}", e);
                        }

                        // Initialize Synapse Manager (Enhanced - Legacy)
                        let synapse_manager = synapse_enhanced::SynapseManager::new(db_arc.clone());
                        if let Err(e) = synapse_manager.initialize_schema().await {
                            eprintln!("[Synapse Enhanced] Failed to initialize Synapse schema: {}", e);
                        }

                        // Initialize New Modular Synapse Integration Gateway
                        let synapse_encryption = Arc::new(synapse::CredentialEncryption::new(key_manager.clone()));
                        let synapse_security = Arc::new(synapse::SecurityBoundary::new(db_arc.clone()));
                        let synapse_plugin_manager = Arc::new(synapse::PluginManager::new(
                            db_arc.clone(),
                            synapse_security.clone(),
                        ));
                        let synapse_connection_manager = Arc::new(synapse::ConnectionManager::new(
                            db_arc.clone(),
                            synapse_encryption.clone(),
                            synapse_security.clone(),
                        ));

                        // Initialize schemas
                        if let Err(e) = synapse_plugin_manager.initialize_schema().await {
                            eprintln!("[Synapse] Failed to initialize plugin schema: {}", e);
                        }
                        if let Err(e) = synapse_connection_manager.initialize_schema().await {
                            eprintln!("[Synapse] Failed to initialize connection schema: {}", e);
                        }
                        if let Err(e) = synapse_security.initialize_schema().await {
                            eprintln!("[Synapse] Failed to initialize security schema: {}", e);
                        }

                        let synapse_state = Arc::new(RwLock::new(synapse::commands::SynapseState {
                            plugin_manager: synapse_plugin_manager,
                            connection_manager: synapse_connection_manager,
                            security: synapse_security,
                        }));
                        handle_for_init.manage(synapse_state);

                        // Initialize Sentry Enhanced
                        let sentry_manager = sentry_enhanced::SentryManager::new(db_arc.clone());
                        if let Err(e) = sentry_manager.initialize_schema().await {
                            eprintln!("[Sentry] Failed to initialize Sentry schema: {}", e);
                        }

                        // Initialize Sentry Auth (session + token management for Sentry)
                        let sentry_auth_manager = sentry_auth::SentryAuthManager::new(
                            authenticator.clone(),
                            db_arc.clone(),
                        );
                        if let Err(e) = sentry_auth_manager.initialize_schema().await {
                            eprintln!("[Sentry Auth] Failed to initialize auth schema: {}", e);
                        }
                        let sentry_auth_state = sentry_auth::SentryAuthState {
                            auth_manager: Arc::new(sentry_auth_manager),
                        };
                        handle_for_init.manage(sentry_auth_state);

                        // Initialize Core Orchestration
                        let core_manager = core::CoreManager::new(db_arc.clone());
                        if let Err(e) = core_manager.initialize_schema().await {
                            eprintln!("[Core] Failed to initialize Core schema: {}", e);
                        }

                        // Initialize Conference Manager
                        let conference_manager = core::ConferenceManager::new(db_arc.clone());
                        if let Err(e) = conference_manager.initialize_schema().await {
                            eprintln!("[Core] Failed to initialize Conference schema: {}", e);
                        }
                        if let Err(e) = conference_manager.initialize_memory_schema().await {
                            eprintln!(
                                "[Core] Failed to initialize Memory Allocation schema: {}",
                                e
                            );
                        }
                        if let Err(e) = conference_manager.initialize_tool_schema().await {
                            eprintln!("[Core] Failed to initialize Tool Closet schema: {}", e);
                        }
                        handle_for_init.manage(conference_manager);

                        // Initialize Project Manager
                        let project_manager = core::ProjectManager::new(db_arc.clone());
                        if let Err(e) = project_manager.initialize_schema().await {
                            eprintln!(
                                "[Core] Failed to initialize Project Management schema: {}",
                                e
                            );
                        }
                        handle_for_init.manage(project_manager);

                        // Manage database for Vault commands (they use Arc<RwLock<Option<DatabaseManager>>> directly)
                        handle_for_init.manage(db_arc);

                        println!("[Alden] Managers initialized successfully");
                        println!("[Alice] Manager initialized successfully");
                        println!("[Vault] Managers initialized successfully");
                        println!("[Mimic] Manager initialized successfully");
                        println!("[Settings] Manager initialized successfully");
                    }
                    Err(e) => {
                        eprintln!("[Database] Failed to initialize: {}", e);
                    }
                }
            });

            // Initialize secure vault
            let vault = app.state::<secure_vault::SecureVault>();
            if let Err(e) = tauri::async_runtime::block_on(vault.initialize(&app_handle)) {
                eprintln!("Failed to initialize secure vault: {}", e);
                let _ = tauri::api::dialog::message(
                    Some(&window),
                    "Vault Initialization Error",
                    &format!("Failed to initialize secure vault: {}", e),
                );
            } else {
                println!("Secure vault initialized successfully");
            }

            // DISABLED: Pure Tauri IPC mode - no HTTP services needed
            // ServiceManager startup commented out for pure IPC architecture
            // Alden, Synapse, and Vault now use direct Tauri IPC commands

            println!("Hearthlink running in pure Tauri IPC mode (no HTTP services)");

            // Set up cleanup on app shutdown
            window.on_window_event({
                move |event| {
                    if let tauri::WindowEvent::CloseRequested { .. } = event {
                        println!("Application closing, stopping all services...");
                    }
                }
            });

            window.set_focus().unwrap();
            window.show().unwrap();

            Ok(())
        })
        .invoke_handler(invoke_handler)
        .run(tauri::generate_context!())
        .expect("error while running tauri application");
}

