  IDLE = 'idle',
  INITIALIZING = 'initializing',
  ACTIVE = 'active',
  CONTEXT_SWITCHING = 'context_switching',
  BREAKOUT_ACTIVE = 'breakout_active',
  INTERRUPTED = 'interrupted',
  ARCHIVING = 'archiving',
  ARCHIVED = 'archived',
  ERROR = 'error'
}

