 * Persona Type Classification
 */
export enum PersonaType {
  CORE = 'core',           // Alden, Alice (system-defined)
  MIMIC = 'mimic',         // User-created Mimic personas
  EXTERNAL = 'external'    // Synapse-mediated external agents
}

/**
 * Session Context - Current state of session state machine
 */
export interface SessionContext {
  currentState: SessionState;
  activePersonaId: string | null;
  previousPersonaId: string | null;
  switchTimestamp: string | null;
  stateMetadata: Record<string, unknown>;
}

