// ============================================================================
// Core - Workflow Orchestration
// ============================================================================

export type WorkflowPatternType =
  | 'sequential'
  | 'parallel'
  | 'loop'
  | 'conditional'
  | 'state_machine'
  | 'rules_driven'
  | 'collaborative';

export interface StateTransition {
  from_state: string;
  to_state: string;
  event: string;
  condition?: string;
  action?: string;
}

export interface ExecutionRule {
  rule_id: string;
  name: string;
  condition: string;
  action: string;
  priority: number;
}

export type CollaborationStrategy =
  | { type: 'voting'; quorum: number }
  | { type: 'parallel_merge'; merge_strategy: string }
  | { type: 'round_robin' }
  | { type: 'leader_follower'; leader: string }
  | { type: 'consensus'; timeout: number };

export type WorkflowPattern =
  | { type: 'sequential' }
  | { type: 'parallel' }
  | { type: 'loop'; max_iterations: number; exit_condition: string }
  | { type: 'conditional'; condition: string; on_true: WorkflowPattern; on_false: WorkflowPattern }
  | { type: 'state_machine'; initial_state: string; transitions: StateTransition[] }
  | { type: 'rules_driven'; rules: ExecutionRule[]; default_action: string }
  | { type: 'collaborative'; strategy: CollaborationStrategy };

export interface RetryConfig {
  max_attempts: number;
  delay_seconds: number;
  backoff_strategy: 'linear' | 'exponential' | 'fixed';
}

export interface WorkflowStep {
  step_id: string;
  name: string;
  description: string;
  assigned_agent: string;
  required_tools: string[];
  inputs: Record<string, JsonValue>;
  outputs: Record<string, JsonValue>;
  timeout?: number;
  retry?: RetryConfig;
}

export type WorkflowState =
  | 'pending'
  | 'running'
  | 'paused'
  | 'completed'
  | { failed: { error: string; failed_step: string } }
  | 'cancelled';

export interface Workflow {
  workflow_id: string;
  name: string;
  description: string;
  pattern: WorkflowPattern;
  steps: WorkflowStep[];
  conference_id?: string;
  state: WorkflowState;
  created_at: string; // Changed to string
  started_at?: string; // Changed to string
  completed_at?: string; // Changed to string
}

export type StepExecutionState =
  | 'pending'
  | 'running'
  | 'completed'
  | 'failed'
  | 'skipped';

