use crate::alden::models::*;
use crate::db_manager::DatabaseManager;

use std::sync::Arc;
use tokio::sync::RwLock;
use uuid::Uuid;

/// Task Management System for Alden Persona
pub struct TaskManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl TaskManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    /// Initialize task tables in database
    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Create tasks table
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS alden_tasks (
                id TEXT PRIMARY KEY NOT NULL,
                user_id TEXT NOT NULL,
                title TEXT NOT NULL,
                description TEXT,
                status TEXT NOT NULL DEFAULT 'pending',
                priority TEXT NOT NULL DEFAULT 'medium',
                due_date INTEGER,
                parent_task_id TEXT,
                tags TEXT,
                created_at INTEGER NOT NULL,
                updated_at INTEGER NOT NULL,
                completed_at INTEGER,
                FOREIGN KEY (parent_task_id) REFERENCES alden_tasks(id) ON DELETE CASCADE
            )
        "#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create alden_tasks table: {}", e))?;

        // Create indexes for performance
        let _ = sqlx::query(
            "CREATE INDEX IF NOT EXISTS idx_tasks_user ON alden_tasks(user_id, status)",
        )
        .execute(&db.pool)
        .await;

        let _ = sqlx::query("CREATE INDEX IF NOT EXISTS idx_tasks_due ON alden_tasks(due_date)")
            .execute(&db.pool)
            .await;

        let _ = sqlx::query(
            "CREATE INDEX IF NOT EXISTS idx_tasks_parent ON alden_tasks(parent_task_id)",
        )
        .execute(&db.pool)
        .await;

        println!("[Alden] Task management schema initialized");
        Ok(())
    }

    /// Create a new task with optional natural language processing
    pub async fn create_task(&self, request: CreateTaskRequest) -> Result<Task, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let task_id = Uuid::new_v4().to_string();
        let now = chrono::Utc::now().timestamp();

        // Process natural language if provided
        let (title, priority, due_date) = if let Some(ref nl) = request.natural_language {
            self.parse_natural_language(nl, &request)
        } else {
            (
                request.title.clone(),
                request
                    .priority
                    .clone()
                    .unwrap_or_else(|| "medium".to_string()),
                request.due_date,
            )
        };

        let tags_json = request
            .tags
            .as_ref()
            .map(|t| serde_json::to_string(t).unwrap_or_else(|_| "[]".to_string()));

        let task = sqlx::query_as::<_, Task>(
            r#"
            INSERT INTO alden_tasks (
                id, user_id, title, description, status, priority,
                due_date, parent_task_id, tags, created_at, updated_at, completed_at
            )
            VALUES (?, ?, ?, ?, 'pending', ?, ?, NULL, ?, ?, ?, NULL)
            RETURNING *
        "#,
        )
        .bind(&task_id)
        .bind(&request.user_id)
        .bind(&title)
        .bind(&request.description)
        .bind(&priority)
        .bind(due_date)
        .bind(tags_json)
        .bind(now)
        .bind(now)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to create task: {}", e))?;

        Ok(task)
    }

    /// Get all tasks with optional filtering
    pub async fn get_tasks(
        &self,
        user_id: String,
        filter: Option<TaskFilter>,
    ) -> Result<Vec<Task>, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let mut query = String::from("SELECT * FROM alden_tasks WHERE user_id = ?");
        let mut params: Vec<String> = vec![user_id];

        if let Some(f) = filter {
            if let Some(statuses) = f.status {
                let placeholders = statuses.iter().map(|_| "?").collect::<Vec<_>>().join(",");
                query.push_str(&format!(" AND status IN ({})", placeholders));
                params.extend(statuses);
            }

            if let Some(priorities) = f.priority {
                let placeholders = priorities.iter().map(|_| "?").collect::<Vec<_>>().join(",");
                query.push_str(&format!(" AND priority IN ({})", placeholders));
                params.extend(priorities);
            }

            if let Some(due_before) = f.due_before {
                query.push_str(" AND due_date <= ?");
                params.push(due_before.to_string());
            }

            if let Some(due_after) = f.due_after {
                query.push_str(" AND due_date >= ?");
                params.push(due_after.to_string());
            }

            if let Some(parent_id) = f.parent_task_id {
                query.push_str(" AND parent_task_id = ?");
                params.push(parent_id);
            }
        }

        query.push_str(" ORDER BY created_at DESC");

        let mut sql_query = sqlx::query_as::<_, Task>(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let tasks = sql_query
            .fetch_all(&db.pool)
            .await
            .map_err(|e| format!("Failed to fetch tasks: {}", e))?;

        Ok(tasks)
    }

    /// Update a task
    pub async fn update_task(
        &self,
        task_id: String,
        user_id: String,
        update: UpdateTaskRequest,
    ) -> Result<Task, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let now = chrono::Utc::now().timestamp();
        let mut updates = Vec::new();
        let mut params: Vec<String> = Vec::new();

        if let Some(title) = update.title {
            updates.push("title = ?");
            params.push(title);
        }

        if let Some(description) = update.description {
            updates.push("description = ?");
            params.push(description);
        }

        if let Some(status) = &update.status {
            updates.push("status = ?");
            params.push(status.clone());

            // Set completed_at if status is completed
            if status == "completed" {
                updates.push("completed_at = ?");
                params.push(now.to_string());
            }
        }

        if let Some(priority) = update.priority {
            updates.push("priority = ?");
            params.push(priority);
        }

        if let Some(due_date) = update.due_date {
            updates.push("due_date = ?");
            params.push(due_date.to_string());
        }

        if let Some(tags) = update.tags {
            let tags_json = serde_json::to_string(&tags).unwrap_or_else(|_| "[]".to_string());
            updates.push("tags = ?");
            params.push(tags_json);
        }

        updates.push("updated_at = ?");
        params.push(now.to_string());

        if updates.is_empty() {
            return Err("No updates provided".to_string());
        }

        let query = format!(
            "UPDATE alden_tasks SET {} WHERE id = ? AND user_id = ? RETURNING *",
            updates.join(", ")
        );

        params.push(task_id);
        params.push(user_id);

        let mut sql_query = sqlx::query_as::<_, Task>(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let task = sql_query
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Failed to update task: {}", e))?;

        Ok(task)
    }

    /// Delete a task (with archive option)
    pub async fn delete_task(
        &self,
        task_id: String,
        user_id: String,
        archive: bool,
    ) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        if archive {
            // Archive by setting status to archived
            sqlx::query("UPDATE alden_tasks SET status = 'archived', updated_at = ? WHERE id = ? AND user_id = ?")
                .bind(chrono::Utc::now().timestamp())
                .bind(&task_id)
                .bind(&user_id)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to archive task: {}", e))?;
        } else {
            // Permanently delete
            sqlx::query("DELETE FROM alden_tasks WHERE id = ? AND user_id = ?")
                .bind(&task_id)
                .bind(&user_id)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to delete task: {}", e))?;
        }

        Ok(())
    }

    /// Add a subtask to a parent task
    pub async fn add_subtask(
        &self,
        parent_id: String,
        user_id: String,
        request: CreateTaskRequest,
    ) -> Result<Task, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Verify parent task exists
        let parent_exists: bool = sqlx::query_scalar(
            "SELECT EXISTS(SELECT 1 FROM alden_tasks WHERE id = ? AND user_id = ?)",
        )
        .bind(&parent_id)
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to verify parent task: {}", e))?;

        if !parent_exists {
            return Err("Parent task not found".to_string());
        }

        let task_id = Uuid::new_v4().to_string();
        let now = chrono::Utc::now().timestamp();

        let tags_json = request
            .tags
            .as_ref()
            .map(|t| serde_json::to_string(t).unwrap_or_else(|_| "[]".to_string()));

        let task = sqlx::query_as::<_, Task>(
            r#"
            INSERT INTO alden_tasks (
                id, user_id, title, description, status, priority,
                due_date, parent_task_id, tags, created_at, updated_at, completed_at
            )
            VALUES (?, ?, ?, ?, 'pending', ?, ?, ?, ?, ?, ?, NULL)
            RETURNING *
        "#,
        )
        .bind(&task_id)
        .bind(&request.user_id)
        .bind(&request.title)
        .bind(&request.description)
        .bind(request.priority.unwrap_or_else(|| "medium".to_string()))
        .bind(request.due_date)
        .bind(&parent_id)
        .bind(tags_json)
        .bind(now)
        .bind(now)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to create subtask: {}", e))?;

        Ok(task)
    }

    /// Get task analytics for productivity insights
    pub async fn get_analytics(&self, user_id: String) -> Result<TaskAnalytics, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Total tasks
        let total_tasks: i64 = sqlx::query_scalar(
            "SELECT COUNT(*) FROM alden_tasks WHERE user_id = ? AND status != 'archived'",
        )
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count tasks: {}", e))?;

        // Completed tasks
        let completed_tasks: i64 = sqlx::query_scalar(
            "SELECT COUNT(*) FROM alden_tasks WHERE user_id = ? AND status = 'completed'",
        )
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count completed tasks: {}", e))?;

        // Pending tasks
        let pending_tasks: i64 = sqlx::query_scalar(
            "SELECT COUNT(*) FROM alden_tasks WHERE user_id = ? AND status = 'pending'",
        )
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count pending tasks: {}", e))?;

        // In progress tasks
        let in_progress_tasks: i64 = sqlx::query_scalar(
            "SELECT COUNT(*) FROM alden_tasks WHERE user_id = ? AND status = 'in_progress'",
        )
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count in-progress tasks: {}", e))?;

        // Overdue tasks
        let now = chrono::Utc::now().timestamp();
        let overdue_tasks: i64 = sqlx::query_scalar(
            "SELECT COUNT(*) FROM alden_tasks WHERE user_id = ? AND status NOT IN ('completed', 'archived') AND due_date < ?"
        )
        .bind(&user_id)
        .bind(now)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count overdue tasks: {}", e))?;

        // Completion rate
        let completion_rate = if total_tasks > 0 {
            (completed_tasks as f64 / total_tasks as f64) * 100.0
        } else {
            0.0
        };

        // Average completion time
        let avg_completion_time: Option<f64> = sqlx::query_scalar(
            "SELECT AVG((completed_at - created_at) / 3600.0) FROM alden_tasks WHERE user_id = ? AND completed_at IS NOT NULL"
        )
        .bind(&user_id)
        .fetch_one(&db.pool)
        .await
        .ok();

        // Tasks by priority
        let priority_rows: Vec<(String, i64)> = sqlx::query_as(
            "SELECT priority, COUNT(*) as count FROM alden_tasks WHERE user_id = ? AND status != 'archived' GROUP BY priority"
        )
        .bind(&user_id)
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to get priority stats: {}", e))?;

        let tasks_by_priority = serde_json::json!(priority_rows
            .into_iter()
            .map(|(p, c)| (p, c))
            .collect::<std::collections::HashMap<_, _>>());

        // Tasks by tag (simplified - would need JSON parsing in production)
        let tasks_by_tag = serde_json::json!({});

        Ok(TaskAnalytics {
            total_tasks,
            completed_tasks,
            pending_tasks,
            in_progress_tasks,
            overdue_tasks,
            completion_rate,
            avg_completion_time_hours: avg_completion_time,
            tasks_by_priority,
            tasks_by_tag,
        })
    }

    /// Simple natural language parser for task creation
    fn parse_natural_language(
        &self,
        nl: &str,
        request: &CreateTaskRequest,
    ) -> (String, String, Option<i64>) {
        let lower = nl.to_lowercase();

        // Extract priority from keywords
        let priority = if lower.contains("urgent") || lower.contains("asap") {
            "urgent".to_string()
        } else if lower.contains("high priority") || lower.contains("important") {
            "high".to_string()
        } else if lower.contains("low priority") {
            "low".to_string()
        } else {
            request
                .priority
                .clone()
                .unwrap_or_else(|| "medium".to_string())
        };

        // Extract due date from keywords (simplified - would use chrono-english in production)
        let due_date = if lower.contains("today") {
            Some(chrono::Utc::now().timestamp())
        } else if lower.contains("tomorrow") {
            Some((chrono::Utc::now() + chrono::Duration::days(1)).timestamp())
        } else if lower.contains("next week") {
            Some((chrono::Utc::now() + chrono::Duration::weeks(1)).timestamp())
        } else {
            request.due_date
        };

        // Use provided title or extract from natural language
        let title = request.title.clone();

        (title, priority, due_date)
    }
}

