use tauri::State;

pub struct AldenState {
    pub task_manager: Arc<RwLock<TaskManager>>,
}

#[tauri::command]
pub async fn alden_tasks_create(
    request: CreateTaskRequest,
    state: State<'_, AldenState>,
) -> Result<Task, String> {
    let manager = state.task_manager.read().await;
    manager.create_task(request).await
}

use serde::Deserialize;

#[derive(Debug, Deserialize)]
pub struct GetTasksRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
    pub filter: Option<TaskFilter>,
}

#[derive(Debug, Deserialize)]
pub struct UpdateTaskCommandRequest {
    #[serde(rename = "taskId")]
    pub task_id: String,
    #[serde(rename = "userId")]
    pub user_id: String,
    pub update: UpdateTaskRequest,
}

#[derive(Debug, Deserialize)]
pub struct DeleteTaskRequest {
    #[serde(rename = "taskId")]
    pub task_id: String,
    #[serde(rename = "userId")]
    pub user_id: String,
    pub archive: Option<bool>,
}

#[derive(Debug, Deserialize)]
pub struct CreateSubtaskRequest {
    #[serde(rename = "parentId")]
    pub parent_id: String,
    #[serde(rename = "userId")]
    pub user_id: String,
    pub request: CreateTaskRequest,
}

#[derive(Debug, Deserialize)]
pub struct GetAnalyticsRequest {
    #[serde(rename = "userId")]
    pub user_id: String,
}

#[tauri::command]
pub async fn alden_tasks_get(
    request: GetTasksRequest,
    state: State<'_, AldenState>,
) -> Result<Vec<Task>, String> {
    let manager = state.task_manager.read().await;
    manager.get_tasks(request.user_id, request.filter).await
}

#[tauri::command]
pub async fn alden_tasks_update(
    request: UpdateTaskCommandRequest,
    state: State<'_, AldenState>,
) -> Result<Task, String> {
    let manager = state.task_manager.read().await;
    manager
        .update_task(request.task_id, request.user_id, request.update)
        .await
}

#[tauri::command]
pub async fn alden_tasks_delete(
    request: DeleteTaskRequest,
    state: State<'_, AldenState>,
) -> Result<(), String> {
    let manager = state.task_manager.read().await;
    manager
        .delete_task(
            request.task_id,
            request.user_id,
            request.archive.unwrap_or(false),
        )
        .await
}

#[tauri::command]
pub async fn alden_tasks_subtasks_create(
    request: CreateSubtaskRequest,
    state: State<'_, AldenState>,
) -> Result<Task, String> {
    let manager = state.task_manager.read().await;
    manager
        .add_subtask(request.parent_id, request.user_id, request.request)
        .await
}

#[tauri::command]
pub async fn alden_tasks_analytics_get(
    request: GetAnalyticsRequest,
    state: State<'_, AldenState>,
) -> Result<TaskAnalytics, String> {
    let manager = state.task_manager.read().await;
    manager.get_analytics(request.user_id).await
}

