            params.push(t);
        }

        if let Some(d) = description {
            updates.push("description = ?");
            params.push(d);
        }

        if let Some(st) = start_time {
            updates.push("start_time = ?");
            params.push(st.to_string());
        }

        if let Some(et) = end_time {
            updates.push("end_time = ?");
            params.push(et.to_string());
        }

        if let Some(l) = location {
            updates.push("location = ?");
            params.push(l);
        }

        let query = format!(
            "UPDATE alden_calendar_events SET {} WHERE id = ? AND user_id = ? RETURNING *",
            updates.join(", ")
        );

        params.push(event_id);
        params.push(user_id);

        let mut sql_query = sqlx::query_as::<_, CalendarEvent>(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let event = sql_query
            .fetch_one(&db.pool)
            .await
            .map_err(|e| format!("Failed to update calendar event: {}", e))?;

        Ok(event)
    }

    /// Delete a calendar event
    pub async fn delete_event(&self, event_id: String, user_id: String) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        sqlx::query("DELETE FROM alden_calendar_events WHERE id = ? AND user_id = ?")
            .bind(&event_id)
            .bind(&user_id)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to delete calendar event: {}", e))?;

        Ok(())
    }

    /// Find available time slots
    pub async fn get_availability(
        &self,
        request: AvailabilityRequest,
    ) -> Result<Vec<AvailabilitySlot>, String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        // Get all events in the time range
        let mut query = String::from(
            "SELECT start_time, end_time FROM alden_calendar_events WHERE user_id = ? AND start_time >= ? AND end_time <= ?"
        );
        let mut params: Vec<String> = vec![
            request.user_id.clone(),
            request.start_time.to_string(),
            request.end_time.to_string(),
        ];

        if let Some(cal_ids) = request.calendar_ids {
            let placeholders = cal_ids.iter().map(|_| "?").collect::<Vec<_>>().join(",");
            query.push_str(&format!(" AND calendar_id IN ({})", placeholders));
            params.extend(cal_ids);
        }

        query.push_str(" ORDER BY start_time ASC");

        let mut sql_query = sqlx::query(&query);
        for param in params {
            sql_query = sql_query.bind(param);
        }

        let rows = sql_query
            .fetch_all(&db.pool)
            .await
            .map_err(|e| format!("Failed to fetch events for availability: {}", e))?;

        // Extract busy periods
        let mut busy_periods: Vec<(i64, i64)> = rows
            .iter()
            .map(|row| {
                let start: i64 = row.get("start_time");
                let end: i64 = row.get("end_time");
                (start, end)
            })
            .collect();

        // Find gaps (available slots)
        let mut available_slots = Vec::new();
        let mut current_time = request.start_time;
        let duration_seconds = request.duration_minutes * 60;

        busy_periods.sort_by_key(|p| p.0);

        for (busy_start, busy_end) in busy_periods {
            // Check if there's a gap before this busy period
            if current_time + duration_seconds <= busy_start {
                available_slots.push(AvailabilitySlot {
                    start_time: current_time,
                    end_time: current_time + duration_seconds,
                });
            }
            current_time = busy_end.max(current_time);
        }

        // Check for availability after the last busy period
        if current_time + duration_seconds <= request.end_time {
            available_slots.push(AvailabilitySlot {
                start_time: current_time,
                end_time: current_time + duration_seconds,
            });
        }

        Ok(available_slots)
    }

