// ============================================================================
// State Management
// ============================================================================

pub struct ChatState {
    pub chat_manager: Arc<ChatManager>,
    pub history: Arc<RwLock<HashMap<String, Vec<ChatMessageEntry>>>>,
    pub typing_status: Arc<RwLock<HashMap<String, bool>>>,
    pub db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl ChatState {
    async fn append_history(&self, user_id: &str, entry: ChatMessageEntry) -> Result<(), String> {
        let mut history = self.history.write().await;
        let entries = history.entry(user_id.to_string()).or_default();
        entries.push(entry.clone());
        if entries.len() > MAX_CHAT_HISTORY {
            let overflow = entries.len() - MAX_CHAT_HISTORY;
            entries.drain(0..overflow);
        }

        self.persist_message(&entry).await
    }

    async fn persist_message(&self, entry: &ChatMessageEntry) -> Result<(), String> {
        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let metadata = entry
            .metadata
            .as_ref()
            .map(|m| serde_json::to_string(m).unwrap_or_default());

        sqlx::query(
            "INSERT OR REPLACE INTO alden_chat_messages (id, user_id, role, content, created_at, session_id, metadata) VALUES (?, ?, ?, ?, ?, ?, ?)",
        )
        .bind(&entry.message_id)
        .bind(&entry.user_id)
        .bind(&entry.role)
        .bind(&entry.content)
        .bind(&entry.created_at)
        .bind(&entry.session_id)
        .bind(metadata)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to persist chat message: {}", e))?;

        Ok(())
    }

    async fn load_history(
        &self,
        user_id: &str,
        limit: Option<usize>,
    ) -> Result<Vec<ChatMessageEntry>, String> {
        // Try memory cache first
        if let Some(cached) = self.history.read().await.get(user_id).cloned() {
            if limit.is_none() || cached.len() >= limit.unwrap_or(0) {
                return Ok(if let Some(limit) = limit {
                    let len = cached.len();
                    let start = len.saturating_sub(limit);
                    cached[start..].to_vec()
                } else {
                    cached
                });
            }
        }

        let mut query = String::from("SELECT id, user_id, role, content, created_at, session_id, metadata FROM alden_chat_messages WHERE user_id = ? ORDER BY created_at DESC");
        if limit.is_some() {
            query.push_str(" LIMIT ?");
        }

        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        let mut sql_query = sqlx::query(&query).bind(user_id);
        if let Some(limit) = limit {
            sql_query = sql_query.bind(limit as i64);
        }

        let rows = sql_query
            .fetch_all(&db.pool)
            .await
            .map_err(|e| format!("Failed to fetch chat history: {}", e))?;

        let mut entries: Vec<ChatMessageEntry> =
            rows.into_iter().map(row_to_chat_entry).collect();
        entries.reverse(); // chronological order

        // Update cache
        self.history
            .write()
            .await
            .insert(user_id.to_string(), entries.clone());

        Ok(entries)
    }

    async fn clear_history(&self, user_id: &str) -> Result<(), String> {
        {
            let mut history = self.history.write().await;
            history.remove(user_id);
        }

        let db_lock = self.db.read().await;
        let db = db_lock.as_ref().ok_or("Database not initialized")?;

        sqlx::query("DELETE FROM alden_chat_messages WHERE user_id = ?")
            .bind(user_id)
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to clear chat history: {}", e))?;

        Ok(())
    }

    async fn set_typing(&self, user_id: &str, is_typing: bool) {
        let mut typing = self.typing_status.write().await;
        typing.insert(user_id.to_string(), is_typing);
    }
}

// ============================================================================
// Tauri Commands
// ============================================================================

/// Handle a chat message from the user
#[tauri::command]
pub async fn alden_chat_handle_message(
    request: HandleMessageRequest,
    state: State<'_, ChatState>,
) -> Result<String, String> {
    state.chat_manager.handle_message(request).await
}

/// Send a chat message via AldenBridge
#[tauri::command]
pub async fn alden_chat_send(
    request: SendChatMessageRequest,
    state: State<'_, ChatState>,
) -> Result<ChatMessageEntry, String> {
    let trimmed = request.message.trim();
    if trimmed.is_empty() {
        return Err("Message cannot be empty".to_string());
    }

    let user_message = ChatMessageEntry::new(&request.user_id, "user", trimmed, &request.session_id);
    state.append_history(&request.user_id, user_message).await?;

    let response_text = state
        .chat_manager
        .handle_message(HandleMessageRequest {
            user_id: request.user_id.clone(),
            message: trimmed.to_string(),
            session_id: request.session_id.clone(),
        })
        .await?;

    let response_entry = ChatMessageEntry::new(
        &request.user_id,
        "alden",
        &response_text,
        &request.session_id,
    );
    state.append_history(&request.user_id, response_entry.clone()).await?;

    Ok(response_entry)
}

/// Retrieve chat history for a user
#[tauri::command]
pub async fn alden_chat_history(
    request: ChatHistoryRequest,
    state: State<'_, ChatState>,
) -> Result<Vec<ChatMessageEntry>, String> {
    state
        .load_history(&request.user_id, request.limit)
        .await
}

/// Clear chat history for a user
#[tauri::command]
pub async fn alden_chat_clear(
    request: ClearChatRequest,
    state: State<'_, ChatState>,
) -> Result<(), String> {
    state.clear_history(&request.user_id).await
}

/// Set typing indicator state for a user
#[tauri::command]
pub async fn alden_chat_set_typing_indicator(
    request: TypingIndicatorRequest,
    state: State<'_, ChatState>,
) -> Result<(), String> {
    state.set_typing(&request.user_id, request.is_typing).await;
    Ok(())
}

