    }

    // Create/update operations
    if (command.includes('create') || command.includes('update')) {
      return {
        success: true,
        id: 'mock-id-' + Date.now(),
        ...params?.request,
        created_at: new Date().toISOString(),
      } as T;
    }

    // Delete operations
    if (command.includes('delete')) {
      return { success: true } as T;
    }

    // Fallback to base implementation
    return super.getMockResponse<T>(command, params);
  }

  /**
   * Initialize Alden bridge - sets up persona memory only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[AldenBridge.onInitialize] START - Initializing Alden bridge');

    // Initialize persona memory with default values
    // This is synchronous and safe to do during initialization
    // Note: userId will be injected from AuthContext via bridge config
    this.personaMemory = {
      persona_id: 'alden',
      user_id: this.userId || 'default_user',
      schema_version: '1.0.0',
      timestamp: new Date().toISOString(),
      traits: {
        openness: 75,
        conscientiousness: 80,
        extraversion: 65,
        agreeableness: 85,
        emotional_stability: 70,
      },
      motivation_style: 'supportive',
      trust_level: 1.0,
      feedback_score: 0,
      learning_agility: 0,
      reflective_capacity: 0,
      habit_consistency: 0,
      engagement: 0,
      correction_events: [],
      session_mood: [],
      relationship_log: [],
      user_tags: [],
      provenance: {
        origin: 'system',
        action: 'initialization',
      },
      last_modified_by: 'system',
      editable_fields: ['traits', 'motivation_style', 'user_tags'],
      audit_log: [],
    };

    // Tasks will be loaded lazily on first getProps() call
    this.tasks = [];
    this.tasksLoaded = false;

    console.log('[AldenBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.personaMemory = null;
    this.tasks = [];
    this.tasksLoaded = false;
  }

  /**
   * Get props for Alden React component
   *
   * Performs lazy loading of tasks on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<AldenPersonaProps> {
    console.log('[AldenBridge.getProps] START - Getting props for React component');
    console.log('[AldenBridge.getProps] personaMemory exists:', !!this.personaMemory);

    if (!this.personaMemory) {
      console.error('[AldenBridge.getProps] ERROR - Persona memory not loaded');
      throw new Error('Persona memory not loaded');
    }

    // Lazy load tasks on first call (safe to use IPC after mount)
    if (!this.tasksLoaded) {
      console.log('[AldenBridge.getProps] Lazy loading tasks (first call after mount)');
      try {
        await this.loadTasks();
        this.tasksLoaded = true;
      } catch (error) {
        console.warn('[AldenBridge.getProps] Failed to load tasks, continuing with empty list:', error);
        // Don't throw - allow degraded mode
        this.tasks = [];
      }
    }

    const props = {
      personaId: this.personaMemory.persona_id,
      traits: this.personaMemory.traits,
      motivationStyle: this.personaMemory.motivation_style,
      trustLevel: this.personaMemory.trust_level,
      feedbackScore: this.personaMemory.feedback_score,
      habitConsistency: this.personaMemory.habit_consistency,
      onTraitUpdate: this.updateTraits.bind(this),
      onExport: this.exportPersonaMemory.bind(this),
      onCorrection: this.recordCorrection.bind(this),
      auditLog: this.personaMemory.audit_log,
    };

    console.log('[AldenBridge.getProps] END - Returning props successfully');
    return props;
  }

