  /**
   * Load all tasks from backend
   */
  private async loadTasks(): Promise<void> {
    console.log('[AldenBridge.loadTasks] START - About to load tasks from backend');
    try {
      // Note: alden_tasks_list expects a request object with userId
      const request = {
        userId: this.userId || 'default_user',
      };
      console.log('[AldenBridge.loadTasks] Calling invokeCommand with:', { command: 'alden_tasks_list', request });

      const startTime = performance.now();
      this.tasks = await this.invokeCommand<Task[]>('alden_tasks_list', { request });
      const elapsed = performance.now() - startTime;

      console.log(`[AldenBridge.loadTasks] SUCCESS - Loaded ${this.tasks.length} tasks in ${elapsed.toFixed(2)}ms`);
    } catch (error) {
      console.error('[AldenBridge.loadTasks] ERROR - Failed to load tasks:', error);
      console.error('[AldenBridge.loadTasks] Error type:', typeof error);
      console.error('[AldenBridge.loadTasks] Error stringified:', JSON.stringify(error, null, 2));
      this.tasks = [];
    }
    console.log('[AldenBridge.loadTasks] END - loadTasks completed');
  }

  /**
   * Get all tasks
   */
  async getTasks(): Promise<BridgeResponse<Task[]>> {
    try {
      await this.loadTasks();
      return { success: true, data: this.tasks };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to fetch tasks',
        code: 'TASK_FETCH_ERROR',
      };
    }
  }

  /**
   * Create a new task
   */
  async createTask(task: CreateTaskRequest): Promise<BridgeResponse<Task>> {
    try {
      // Ensure userId is set (use provided or fallback)
      const request = {
        ...task,
        userId: task.userId || this.userId || 'default_user',
      };

      const created = await this.invokeCommand<Task>('alden_tasks_create', { request });

      this.tasks.push(created);
      this.notifySubscribers({ type: 'task_created', data: created });

      return { success: true, data: created };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to create task',
        code: 'TASK_CREATE_ERROR',
      };
    }
  }

  /**
   * Update a task
   */
  async updateTask(taskId: string, updates: TaskUpdates): Promise<BridgeResponse<Task>> {
    try {
      const request = {
        userId: this.userId || 'default_user',
        taskId,
        ...updates,
      };

      const updated = await this.invokeCommand<Task>('alden_tasks_update', { request });

      const index = this.tasks.findIndex(t => t.id === taskId);
      if (index !== -1) {
        this.tasks[index] = updated;
      }

      this.notifySubscribers({ type: 'task_updated', data: updated });

      return { success: true, data: updated };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to update task',
        code: 'TASK_UPDATE_ERROR',
      };
    }
  }

  /**
   * Delete a task
   */
  async deleteTask(taskId: string): Promise<BridgeResponse<void>> {
    try {
      const request = {
        userId: this.userId || 'default_user',
        taskId,
      };

      await this.invokeCommand('alden_tasks_delete', { request });

      this.tasks = this.tasks.filter(t => t.id !== taskId);
      this.notifySubscribers({ type: 'task_deleted', data: taskId });

      return { success: true, data: undefined };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to delete task',
        code: 'TASK_DELETE_ERROR',
      };
    }
  }

