  /**
   * Send an email
   */
  async sendEmail(request: any): Promise<BridgeResponse<any>> {
    try {
      const toField = request.to_addresses ?? request.to;
      const toArray = Array.isArray(toField) ? toField : [toField];

      const emailRequest: any = {
        account_id: request.account_id,
        user_id: this.userId,
        to_addresses: toArray.filter((entry: string) => !!entry),
        subject: request.subject,
      };
      const bodyPlain = request.body_plain ?? request.body;
      if (bodyPlain) emailRequest.body_plain = bodyPlain;
      if (request.cc || request.cc_addresses) {
        emailRequest.cc_addresses = request.cc_addresses ?? request.cc;
      }
      if (request.bcc || request.bcc_addresses) {
        emailRequest.bcc_addresses = request.bcc_addresses ?? request.bcc;
      }
      if (request.body_html) emailRequest.body_html = request.body_html;

      const result = await this.invokeCommand<any>('alden_email_send_create', { request: emailRequest });
      this.notifySubscribers({ type: 'email_sent', data: result });
      return { success: true, data: result };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to send email',
        code: 'EMAIL_SEND_ERROR',
      };
    }
  }

