  async executeCommand(command: string): Promise<BridgeResponse<CommandResult>> {
    const trimmed = command.trim();
    if (!trimmed) {
      return {
        success: false,
        error: 'Command cannot be empty',
        code: 'CLI_COMMAND_EMPTY',
      };
    }

    try {
      const request = {
        user_id: this.userId,
        command: trimmed,
      };

      const result = await this.invokeCommand<CommandResult>('alden_cli_execute', { request });
      return { success: true, data: result };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to execute command',
        code: 'CLI_EXECUTE_ERROR',
      };
    }
  }

