    startTime?: string;
    endTime?: string;
  }): Promise<BridgeResponse<CalendarEvent[]>> {
    try {
      const request: any = {
        userId: params.userId,
      };
      if (params.startTime) {
        request.startDate = params.startTime;
      }
      if (params.endTime) {
        request.endDate = params.endTime;
      }

      const events = await invoke<CalendarEvent[]>('alden_calendar_list', { request });
      return { success: true, data: events };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to fetch calendar events',
        code: 'CALENDAR_FETCH_ERROR',
      };
    }
  }

  static async createEmailAccount(request: any): Promise<BridgeResponse<EmailAccount>> {
    const result = await invoke<EmailAccount>('alden_email_accounts_create', { request });
    return { success: true, data: result };
  }

  static async sendEmail(request: any): Promise<any> {
    return invoke('alden_email_send_create', { request });
  }

  static async getEmailAccounts(userId: string): Promise<BridgeResponse<EmailAccount[]>> {
    const request = { user_id: userId };
    const accounts = await invoke<any[]>('alden_email_accounts_get', { request });
    const normalizedAccounts: EmailAccount[] = accounts.map(account => ({
      id: account.account_id,
      email_address: account.email_address,
      display_name: account.display_name,
      protocol: account.protocol,
      server: account.server,
      port: account.port,
      ssl_enabled: account.ssl_enabled,
      last_sync: account.last_sync, // Assuming this is already ISO string
      status: account.status,
    }));
    return { success: true, data: normalizedAccounts };
  }

  static async getEmailMessages(userId: string, accountId: string, folder?: string, limit?: number): Promise<BridgeResponse<EmailMessage[]>> {
    const request: any = { user_id: userId, account_id: accountId };
    if (folder) request.folder = folder;
    if (limit) request.limit = limit;
    const messages = await invoke<any[]>('alden_email_messages_get', { request });
    const normalizedMessages: EmailMessage[] = messages.map(message => ({
      id: message.message_id,
      account_id: message.account_id,
      from: message.from,
      to: message.to,
      cc: message.cc,
      bcc: message.bcc,
      subject: message.subject,
      body: message.body,
      body_html: message.body_html,
      received_date: message.received_date, // Assuming this is already ISO string
      read: message.read,
      starred: message.starred,
      folder: message.folder,
      labels: message.labels,
      attachments: message.attachments,
    }));
    return { success: true, data: normalizedMessages };
  }

  static async syncEmailAccount(userId: string, accountId: string): Promise<number> {
    const request = { user_id: userId, account_id: accountId };
    return invoke<number>('alden_email_sync_account', { request });
  }

  static async setRecurringTask(request: { task_id: string; rrule: string }): Promise<Task> {
    return invoke<Task>('alden_tasks_set_recurring', { request });
  }
}

