/// Get security summary statistics
pub async fn synapse_get_security_summary(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
) -> Result<HashMap<String, serde_json::Value>, String> {
    let db = db.read().await;
    let db = db.as_ref().ok_or("Database not initialized")?;

    let mut summary = HashMap::new();

    // Total logs count
    let total_query = r#"SELECT COUNT(*) as count FROM synapse_security_logs"#;
    let total: (i64,) = sqlx::query_as(total_query)
        .fetch_one(&db.pool)
        .await
        .map_err(|e| format!("Failed to count logs: {}", e))?;
    summary.insert(
        "total_logs".to_string(),
        serde_json::Value::Number(total.0.into()),
    );

    // Count by severity
    let severity_query = r#"
        SELECT severity, COUNT(*) as count
        FROM synapse_security_logs
        GROUP BY severity
    "#;
    let severity_rows: Vec<(String, i64)> = sqlx::query_as(severity_query)
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to count by severity: {}", e))?;

    let mut by_severity = HashMap::new();
    for (severity, count) in severity_rows {
        by_severity.insert(severity, serde_json::Value::Number(count.into()));
    }
    summary.insert(
        "by_severity".to_string(),
        serde_json::Value::Object(by_severity.into_iter().map(|(k, v)| (k, v)).collect()),
    );

    // Count by event type
    let event_query = r#"
        SELECT event_type, COUNT(*) as count
        FROM synapse_security_logs
        GROUP BY event_type
    "#;
