pub async fn initialize_schema(db: &DatabaseManager) -> Result<(), String> {
    sqlx::query(
        r#"
        CREATE TABLE IF NOT EXISTS alden_chat_messages (
            id TEXT PRIMARY KEY NOT NULL,
            user_id TEXT NOT NULL,
            role TEXT NOT NULL,
            content TEXT NOT NULL,
            created_at TEXT NOT NULL,
            session_id TEXT NOT NULL,
            metadata TEXT
        )
        "#,
    )
    .execute(&db.pool)
    .await
    .map_err(|e| format!("Failed to create alden_chat_messages table: {}", e))?;

    let _ = sqlx::query(
        "CREATE INDEX IF NOT EXISTS idx_chat_user ON alden_chat_messages(user_id, created_at)",
    )
    .execute(&db.pool)
    .await;

    Ok(())
}

fn row_to_chat_entry(row: SqliteRow) -> ChatMessageEntry {
    let metadata: Option<String> = row.get("metadata");
    let parsed_metadata = metadata
        .and_then(|raw| serde_json::from_str(&raw).ok());

    ChatMessageEntry {
        message_id: row.get::<String, _>("id"),
        user_id: row.get::<String, _>("user_id"),
        role: row.get::<String, _>("role"),
        content: row.get::<String, _>("content"),
        created_at: row.get::<String, _>("created_at"),
        session_id: row.get::<String, _>("session_id"),
        metadata: parsed_metadata,
    }
}

