      };
      console.log('[AldenBridge.loadTasks] Calling invokeCommand with:', { command: 'alden_tasks_list', request });

      const startTime = performance.now();
      this.tasks = await this.invokeCommand<Task[]>('alden_tasks_list', { request });
      const elapsed = performance.now() - startTime;

      console.log(`[AldenBridge.loadTasks] SUCCESS - Loaded ${this.tasks.length} tasks in ${elapsed.toFixed(2)}ms`);
    } catch (error) {
      console.error('[AldenBridge.loadTasks] ERROR - Failed to load tasks:', error);
      console.error('[AldenBridge.loadTasks] Error type:', typeof error);
      console.error('[AldenBridge.loadTasks] Error stringified:', JSON.stringify(error, null, 2));
      this.tasks = [];
    }
    console.log('[AldenBridge.loadTasks] END - loadTasks completed');
  }

