   */
  async deleteTask(taskId: string): Promise<BridgeResponse<void>> {
    try {
      const request = {
        userId: this.userId || 'default_user',
        taskId,
      };

      await this.invokeCommand('alden_tasks_delete', { request });

      this.tasks = this.tasks.filter(t => t.id !== taskId);
      this.notifySubscribers({ type: 'task_deleted', data: taskId });

      return { success: true, data: undefined };
