   */
  static isAuthenticated(): boolean {
    return this.userId !== null && this.authToken !== null;
  }

  /**
   * Clear current credentials
   */
  static clearCredentials(): void {
    this.userId = null;
    this.authToken = null;
    console.log('[AuthClient] Credentials cleared');
  }

  /**
   * Generate demo credentials for testing
   */
  static generateDemoCredentials(): { userId: string; authToken: string } {
    const userId = `demo_user_${Date.now()}`;

    // Create a simple JWT-like token that matches the backend's expected format
    // This is for demo purposes only - in production, proper JWT libraries should be used
    const header = btoa(JSON.stringify({ alg: "HS256", typ: "JWT" }));
    const payload = btoa(JSON.stringify({
      sub: userId,
      exp: Math.floor(Date.now() / 1000) + (60 * 60) // 1 hour expiration
    }));

    // For demo purposes, create a simple signature (in production, use proper HMAC)
    const signature = btoa("demo_signature_" + Math.random().toString(36).substring(2));
    const authToken = `${header}.${payload}.${signature}`;

    this.setCredentials(userId, authToken);

    console.log('[AuthClient] Generated demo credentials:', {
      userId,
      tokenLength: authToken.length,
      tokenFormat: 'JWT-like'
    });

    return { userId, authToken };
  }
}

