    console.log('[SentryClient] Acknowledging alert:', alertId);

    try {
      const result = await invoke('sentry_acknowledge_alert', { alertId });
      console.log('[SentryClient] Alert acknowledged:', result);
      return result as boolean;
    } catch (error) {
      console.error('[SentryClient] Acknowledge alert error:', error);
      throw new Error(`Failed to acknowledge alert: ${error}`);
    }
  }

  /**
   * Get system resource usage
   */
  static async getSystemResources(): Promise<any> {
    console.log('[SentryClient] Getting system resources...');

    try {
      const resources = await invoke('sentry_get_system_resources');
      console.log('[SentryClient] System resources received:', resources);
      return resources;
    } catch (error) {
      console.error('[SentryClient] System resources error:', error);
      // Return empty object for "Not Connected" state
      return {};
    }
  }

  /**
   * Get performance metrics summary
   */
  static async getPerformanceMetrics(): Promise<any> {
    console.log('[SentryClient] Getting performance metrics...');

    try {
      const metrics = await invoke('sentry_get_performance_metrics');
      console.log('[SentryClient] Performance metrics received:', metrics);
      return metrics;
    } catch (error) {
      console.error('[SentryClient] Performance metrics error:', error);
      // Return empty object for "Not Connected" state
      return {};
    }
  }
}

