export class SynapseClient {
  /**
   * Create a new user pattern insight
   */
  static async createInsight(
    discoveringPersona: string,
    patternTitle: string,
    patternDetails: any,
    confidence: number,
    tags?: string[]
  ): Promise<any> {
    const { userId } = await HearthlinkClient.initialize();

    console.log('[SynapseClient] Creating insight:', patternTitle);

    try {
      const result = await invoke('synapse_create_insight', {
        userId,
        discoveringPersona,
        patternTitle,
        patternDetails,
        confidence,
        tags
      });

      console.log('[SynapseClient] Insight created:', result);
      return result;
    } catch (error) {
      console.error('[SynapseClient] Create insight error:', error);
      throw new Error(`Failed to create insight: ${error}`);
    }
  }

  /**
   * Get relevant insights for current context
   */
  static async getInsights(
    requestingPersona: string,
    currentContext: any,
    maxInsights?: number
  ): Promise<any[]> {
    const { userId } = await HearthlinkClient.initialize();

    console.log('[SynapseClient] Getting insights for:', requestingPersona);

    try {
      const insights = await invoke('synapse_get_insights', {
        userId,
        requestingPersona,
        currentContext,
        maxInsights
      }) as any[];

      console.log('[SynapseClient] Retrieved', insights.length, 'insights');
      return insights;
    } catch (error) {
      console.error('[SynapseClient] Get insights error:', error);
      throw new Error(`Failed to get insights: ${error}`);
    }
  }

  /**
