export type VoiceMode = 'push-to-talk' | 'active-listening' | 'disabled';

export interface VoiceInputState {
  isListening: boolean;
  isProcessing: boolean;
  transcript: string;
  finalTranscript: string;
  error: string | null;
  mode: VoiceMode;
  activeAgent: string;
  confidence: number;
  audioLevel: number;
}

export interface UseVoiceInputOptions {
  defaultAgent?: string;
  defaultMode?: VoiceMode;
  onTranscript?: (transcript: string, agent: string) => void;
  onFinalTranscript?: (transcript: string, agent: string) => void;
  onAgentChange?: (agent: string) => void;
  onModeChange?: (mode: VoiceMode) => void;
}

// Speech Recognition interface extension
interface ExtendedSpeechRecognition extends SpeechRecognition {
  continuous: boolean;
  interimResults: boolean;
  lang: string;
  maxAlternatives: number;
}

declare global {
  interface Window {
    webkitSpeechRecognition: new () => ExtendedSpeechRecognition;
    SpeechRecognition: new () => ExtendedSpeechRecognition;
  }
}

export const useVoiceInput = (options: UseVoiceInputOptions = {}) => {
  const {
    defaultAgent = 'alden',
    defaultMode = 'push-to-talk',
    onTranscript,
    onFinalTranscript,
    onAgentChange,
    onModeChange
  } = options;

  // State
  const [state, setState] = useState<VoiceInputState>({
    isListening: false,
    isProcessing: false,
    transcript: '',
    finalTranscript: '',
    error: null,
    mode: defaultMode,
    activeAgent: defaultAgent,
    confidence: 0,
    audioLevel: 0
  });

  // Refs
  const recognitionRef = useRef<ExtendedSpeechRecognition | null>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const microphoneRef = useRef<MediaStreamAudioSourceNode | null>(null);
  const animationFrameRef = useRef<number | null>(null);

  // Check browser support
  const isBrowserSupported = useCallback(() => {
    return !!(window.SpeechRecognition || window.webkitSpeechRecognition);
  }, []);

  // Initialize speech recognition
  const initializeSpeechRecognition = useCallback(() => {
    if (!isBrowserSupported()) {
      setState(prev => ({ ...prev, error: 'Speech recognition not supported in this browser' }));
      return false;
    }

    try {
      const SpeechRecognitionClass = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognitionClass();

      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'en-US';
      recognition.maxAlternatives = 1;

      recognition.onstart = () => {
        console.log('[Voice] Recognition started');
        setState(prev => ({ ...prev, isListening: true, error: null }));
      };

      recognition.onresult = (event: SpeechRecognitionEvent) => {
        let interimTranscript = '';
        let finalTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const result = event.results[i];
          const transcript = result[0].transcript;
          const confidence = result[0].confidence;

          if (result.isFinal) {
            finalTranscript += transcript;
            setState(prev => ({
              ...prev,
              finalTranscript: prev.finalTranscript + transcript,
              confidence
            }));

            // Call callback with final transcript
            if (onFinalTranscript) {
              onFinalTranscript(finalTranscript, state.activeAgent);
            }
          } else {
            interimTranscript += transcript;
            setState(prev => ({ ...prev, transcript: interimTranscript, confidence }));

            // Call callback with interim transcript
            if (onTranscript) {
              onTranscript(interimTranscript, state.activeAgent);
            }
          }
        }
      };

      recognition.onerror = (event: SpeechRecognitionErrorEvent) => {
        console.error('[Voice] Recognition error:', event.error);
        setState(prev => ({
          ...prev,
          error: `Speech recognition error: ${event.error}`,
          isListening: false
        }));
      };

      recognition.onend = () => {
        console.log('[Voice] Recognition ended');
        setState(prev => ({ ...prev, isListening: false }));

        // Restart if in active listening mode
        if (state.mode === 'active-listening' && !state.error) {
          setTimeout(() => {
            recognition.start();
          }, 100);
        }
      };

      recognitionRef.current = recognition;
      return true;
    } catch (error) {
      console.error('[Voice] Failed to initialize speech recognition:', error);
      setState(prev => ({ ...prev, error: 'Failed to initialize speech recognition' }));
      return false;
    }
  }, [isBrowserSupported, onTranscript, onFinalTranscript, state.activeAgent, state.mode, state.error]);

  // Initialize audio level monitoring
  const initializeAudioLevel = useCallback(async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioContextRef.current = new AudioContext();
      analyserRef.current = audioContextRef.current.createAnalyser();
      microphoneRef.current = audioContextRef.current.createMediaStreamSource(stream);

      analyserRef.current.fftSize = 256;
      microphoneRef.current.connect(analyserRef.current);

      const bufferLength = analyserRef.current.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);

      const updateAudioLevel = () => {
        if (analyserRef.current && state.isListening) {
          analyserRef.current.getByteFrequencyData(dataArray);
          const average = dataArray.reduce((sum, value) => sum + value, 0) / bufferLength;
          const normalizedLevel = average / 255;

          setState(prev => ({ ...prev, audioLevel: normalizedLevel }));
          animationFrameRef.current = requestAnimationFrame(updateAudioLevel);
        }
      };

      updateAudioLevel();
    } catch (error) {
      console.error('[Voice] Failed to initialize audio level monitoring:', error);
    }
  }, [state.isListening]);

  // Start listening
  const startListening = useCallback(() => {
    if (!recognitionRef.current) {
      if (!initializeSpeechRecognition()) return;
    }

    setState(prev => ({ ...prev, transcript: '', finalTranscript: '', error: null }));

    try {
      recognitionRef.current?.start();
      initializeAudioLevel();
    } catch (error) {
      console.error('[Voice] Failed to start listening:', error);
      setState(prev => ({ ...prev, error: 'Failed to start voice recognition' }));
    }
  }, [initializeSpeechRecognition, initializeAudioLevel]);

  // Stop listening
  const stopListening = useCallback(() => {
    recognitionRef.current?.stop();

    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }

    setState(prev => ({ ...prev, isListening: false, audioLevel: 0 }));
  }, []);

  // Toggle listening based on mode
  const toggleListening = useCallback(() => {
    if (state.isListening) {
      stopListening();
    } else {
      startListening();
    }
  }, [state.isListening, startListening, stopListening]);

  // Change voice mode
  const setMode = useCallback((mode: VoiceMode) => {
    setState(prev => ({ ...prev, mode }));

    if (mode === 'disabled') {
      stopListening();
    } else if (mode === 'active-listening' && !state.isListening) {
      startListening();
    }

    if (onModeChange) {
      onModeChange(mode);
    }
  }, [stopListening, startListening, state.isListening, onModeChange]);

  // Change active agent
  const setActiveAgent = useCallback((agent: string) => {
    setState(prev => ({ ...prev, activeAgent: agent }));

    if (onAgentChange) {
      onAgentChange(agent);
    }
  }, [onAgentChange]);

  // Clear transcript
  const clearTranscript = useCallback(() => {
    setState(prev => ({ ...prev, transcript: '', finalTranscript: '' }));
  }, []);

  // Get current agent voice characteristics
  const getActiveAgentVoice = useCallback(() => {
    return PERSONA_VOICES[state.activeAgent] || PERSONA_VOICES.alden;
  }, [state.activeAgent]);

  // Speak with agent voice
  const speakWithAgentVoice = useCallback((text: string, agent?: string) => {
    const targetAgent = agent || state.activeAgent;
    const voiceChar = PERSONA_VOICES[targetAgent] || PERSONA_VOICES.alden;

    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);

      // Apply voice characteristics
      utterance.pitch = voiceChar.voiceConfig.pitch;
      utterance.rate = voiceChar.voiceConfig.rate;
      utterance.volume = voiceChar.voiceConfig.volume;

      // Try to use preferred voice
      const voices = speechSynthesis.getVoices();
      const preferredVoice = voices.find(voice =>
        voice.name.includes(voiceChar.voiceConfig.voice || '') ||
        voice.lang === 'en-US'
      );

      if (preferredVoice) {
        utterance.voice = preferredVoice;
      }

      speechSynthesis.speak(utterance);
    }
  }, [state.activeAgent]);

  // Initialize on mount
  useEffect(() => {
    initializeSpeechRecognition();

    return () => {
      recognitionRef.current?.stop();
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      audioContextRef.current?.close();
    };
  }, [initializeSpeechRecognition]);

  return {
    // State
    ...state,

    // Actions
    startListening,
    stopListening,
    toggleListening,
    setMode,
    setActiveAgent,
    clearTranscript,
    speakWithAgentVoice,

    // Utilities
    isBrowserSupported: isBrowserSupported(),
    getActiveAgentVoice,
    availableAgents: Object.values(PERSONA_VOICES),

    // Voice characteristics
    PERSONA_VOICES
  };
};

